    return direction === 'rtl' ? 'right' : 'left'
  }

  function getEndEdge(): AxisEdgeType {
    if (scroll === 'y') return 'bottom'
    return direction === 'rtl' ? 'left' : 'right'
  }

  const self: AxisType = {
    scroll,
    cross,
    startEdge,
    endEdge,
    measureSize,
  }
