import React, { useState, useEffect } from 'react';
import CardSmall from '../CardSmall';

// Define DogType and other types as needed
type DogType = {
	_id: string;
	name: string;
	breed: string;
	age: number;
	gender: string;
	place?: string;
	location?: string;
	images: string[];
	adoptionStatus?: string;
	adoptionQueue?: string[];
	vaccinated?: boolean;
	video?: { url?: string };
};

type MapModalType = {
	place: string;
	coords: { lat: number; lon: number };
};

function DogList() {
	const [dogs, setDogs] = useState<DogType[]>([]);
	const [viewDog, setViewDog] = useState<DogType | null>(null);
	// Removed unused editDog and sliderIndex state
	const [mapModal, setMapModal] = useState<MapModalType | null>(null);

	useEffect(() => {
		// Fetch dogs from backend
		fetch('http://localhost:3001/api/dogs')
			.then(res => res.json())
			.then(data => {
				const mapped = Array.isArray(data)
					? data.map((dog: any) => ({
							...dog,
							place: dog.place || dog.location,
							gender: dog.gender || (dog.gender === undefined ? '' : dog.gender),
							images: Array.isArray(dog.images) ? dog.images : [],
						}))
					: [];
				setDogs(mapped);
			});
	}, []);

	// Removed unused toAbsUrl

	const handleShowMap = async (place: string) => {
		// Use Nominatim to geocode
		const res = await fetch(
			`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(place)}`
		);
		const data = await res.json();
		if (data && data.length > 0) {
			setMapModal({
				place,
				coords: { lat: parseFloat(data[0].lat), lon: parseFloat(data[0].lon) },
			});
		} else {
			alert('Location not found');
		}
	};

	// Removed unused handleDogUpdate

	// Removed unused handleRemove

	return (
		<>
			{/* Dog list grid */}
			<div style={{ display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center', margin: '32px 0' }}>
				{dogs.map(dog => {
					// Fix video prop: only pass if url is defined
					const { video, ...rest } = dog;
					const cardProps = video && video.url ? { ...rest, video: { url: video.url } } : rest;
					return (
						<CardSmall
							key={dog._id}
							{...cardProps}
							onViewDetails={() => setViewDog(dog)}
						/>
					);
				})}
			</div>

			{/* Dog details modal (example) */}
			{viewDog && (
				<div>
					<h2 style={{ marginBottom: 16 }}>{viewDog.name}</h2>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Breed:</strong> {viewDog.breed}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Age:</strong> {viewDog.age}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Gender:</strong> {viewDog.gender}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}>
						<strong>Place:</strong>
						{viewDog.place && (
							<span
								style={{ color: '#1976d2', textDecoration: 'underline', cursor: 'pointer', marginLeft: 4 }}
								onClick={() => handleShowMap(viewDog.place)}
							>
								{viewDog.place}
							</span>
						)}
					</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Status:</strong> {viewDog.adoptionStatus}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Adoption Queue:</strong> {viewDog.adoptionQueue && viewDog.adoptionQueue.length ? viewDog.adoptionQueue.join(', ') : 'None'}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Vaccinated:</strong> {viewDog.vaccinated ? 'Yes' : 'No'}</div>
					<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Video:</strong> {viewDog.video && viewDog.video.url ? viewDog.video.url : 'None'}</div>
					<div style={{ display: 'flex', gap: 12, marginTop: 16 }}>
						<button type="button" style={{ background: '#dbb69d', color: '#fff', border: 'none', fontWeight: 600, padding: '8px 18px', borderRadius: 6, cursor: 'pointer' }} onClick={() => alert('Added to wishlist!')}>Add to Wishlist</button>
						<button type="button" style={{ background: '#43a047', color: '#fff', border: 'none', fontWeight: 600, padding: '8px 18px', borderRadius: 6, cursor: 'pointer' }} onClick={() => alert('Adopt functionality coming soon!')}>Adopt</button>
					</div>
					<div style={{ marginTop: 16 }}>
						<button type="button" style={{ background: '#e74c3c', color: '#fff', border: 'none', fontWeight: 600, padding: '8px 18px', borderRadius: 6, cursor: 'pointer' }} onClick={() => setViewDog(null)}>
							Close
						</button>
					</div>
				</div>
			)}

			{/* Map modal (global, always top-level) */}
			{mapModal && !viewDog && (
				<div
					style={{
						position: 'fixed',
						top: '50%',
						left: '50%',
						transform: 'translate(-50%, -50%)',
						background: '#fff',
						border: '2px solid #3498db',
						borderRadius: 16,
						zIndex: 2147483647,
						minWidth: 320,
						maxWidth: 640,
						padding: 16,
						width: '90vw',
						maxHeight: '90vh',
						boxSizing: 'border-box',
						overflow: 'auto',
					}}
				>
					<iframe
						title="Map"
						width="100%"
						height="250"
						style={{ borderRadius: 12, border: '1px solid #ccc', marginTop: 16, zIndex: 1 }}
						src={`https://www.openstreetmap.org/export/embed.html?bbox=${mapModal.coords.lon-0.01},${mapModal.coords.lat-0.01},${mapModal.coords.lon+0.01},${mapModal.coords.lat+0.01}&layer=mapnik&marker=${mapModal.coords.lat},${mapModal.coords.lon}`}
						allowFullScreen
					/>
					<div
						style={{
							position: 'absolute',
							top: 0,
							right: 0,
							zIndex: 2147483647,
							background: 'rgba(255,255,255,0.98)',
							borderBottomLeftRadius: 16,
							padding: 4,
							boxShadow: '0 2px 12px rgba(0,0,0,0.22)',
							pointerEvents: 'auto',
						}}
					>
						<button
							type="button"
							onClick={() => setMapModal(null)}
							style={{
								width: 44,
								height: 44,
								background: '#fff',
								border: '3px solid #e74c3c',
								color: '#e74c3c',
								borderRadius: '50%',
								fontSize: '2rem',
								fontWeight: 'bold',
								cursor: 'pointer',
								zIndex: 2147483647,
								display: 'flex',
								alignItems: 'center',
								justifyContent: 'center',
								touchAction: 'manipulation',
								pointerEvents: 'auto',
							}}
							aria-label="Close"
							title="Close"
						>
							√ó
						</button>
					</div>
					<div style={{ fontSize: '0.9rem', marginTop: 4 }}>
						<a
							href={`https://www.openstreetmap.org/?mlat=${mapModal.coords.lat}&mlon=${mapModal.coords.lon}#map=16/${mapModal.coords.lat}/${mapModal.coords.lon}`}
							target="_blank"
							rel="noopener noreferrer"
						>
							View larger map
						</a>
					</div>
				</div>
			)}
		</>
	);
}

export default DogList;
