// TEMP: Function to send an adoption message for a given dogId
// (moved below imports, inside ChatApp component)
import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import io from 'socket.io-client';
import { useAuth } from '../contexts/AuthContext';
import '../css/chat-app.css';
import '../styles/chatAppNotification.css';

interface UserWithBlocks {
  _id: string;
  blockedUsers?: string[];
}
interface Message {
  _id: string;
  sender: string;
  recipient: string;
  message: string;
  sentAt: string;
  messageType?: string;
  dogId?: string;
  isOwner?: boolean;
}
interface Conversation {
  _id: string;
  participants: string[];
}
const getApiUrl = () => {
  // Try to use explicit env var first
  if (process.env.REACT_APP_API_URL) return process.env.REACT_APP_API_URL;
  // If running on mobile device, try to use window.location.hostname or fallback to LAN IP
  const hostname = window.location.hostname;
  if (window.location.protocol === 'https:') {
    return `https://${hostname}`;
  }
  if (hostname === 'localhost' || hostname === '127.0.0.1') {
    return `http://${hostname}:3001`;
  }
  // If on mobile, try to use LAN IP or fallback to current hostname
  if (/^\d+\.\d+\.\d+\.\d+$/.test(hostname)) {
    return `http://${hostname}:3001`;
  }
  // Fallback to hardcoded LAN IP (update as needed for your network)
  return `http://192.168.1.100:3001`;
};

interface ChatAppProps {
  dogId?: string;
}

const ChatApp: React.FC<ChatAppProps> = ({ dogId }) => {
  const [showChat, setShowChat] = useState(false);
  // Debug: log API URL and socket connection
  useEffect(() => {
    // ...existing code...
  }, [t]);
    // TEMP: Function to send an adoption message for a given dogId
    const sendAdoptionMessage = async (dogId: string) => {
      if (!selectedConvo || !token) return;
      const recipient = selectedConvo.participants.find(id => id !== user._id);
      const messagePayload: any = {
        conversationId: selectedConvo._id,
        sender: user._id,
        recipient,
        message: t('adoptionRequest') || 'Adoption request',
        messageType: 'adoption',
        dogId,
        isOwner: adoptionIsOwner
      };
      socketRef.current?.emit('sendMessage', messagePayload);
      await fetch(`${getApiUrl()}/api/chat/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(messagePayload)
      });
      setMessages(prev => [
        ...prev,
        {
          _id: Math.random().toString(36).substr(2, 9),
          sender: user._id,
          recipient,
          message: messagePayload.message,
          sentAt: new Date().toISOString(),
          messageType: 'adoption',
          dogId,
          isOwner: adoptionIsOwner
        }
      ]);
      setAdoptionDogId(dogId);
    };
  const { t } = useTranslation();
  const { user, token } = useAuth();
  const [dogStatusMap, setDogStatusMap] = useState<Record<string, string>>({});
  const [dogOwnerMap, setDogOwnerMap] = useState<Record<string, string>>({});
  const [adoptionDogId, setAdoptionDogId] = useState('');
  // Listen for 'dog-adopt-initiate' event to set adoptionDogId from anywhere in the app
  useEffect(() => {
    const handler = (e: any) => {
      if (e && e.detail && e.detail.dogId) {
        setAdoptionDogId(e.detail.dogId);
      }
    };
    window.addEventListener('dog-adopt-initiate', handler);
    return () => window.removeEventListener('dog-adopt-initiate', handler);
  }, []);
    // If dogId prop changes, update adoptionDogId
    useEffect(() => {
      if (dogId) setAdoptionDogId(dogId);
    }, [dogId]);
  const [adoptionIsOwner, setAdoptionIsOwner] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [onlineUsers, setOnlineUsers] = useState<{_id: string, userName: string, profilePicture?: string}[]>([]);
  const [selectedConvo, setSelectedConvo] = useState<Conversation | null>(null);
  const [input, setInput] = useState('');
  const [typing] = useState(false);
  const [notification, setNotification] = useState<string | null>(null);
  const [userSearch, setUserSearch] = useState('');
  const socketRef = useRef<ReturnType<typeof io> | null>(null);
  const userWithBlocks = user as UserWithBlocks | null;

  // DEBUG: Log mount and key state after variables are defined
  // eslint-disable-next-line no-console
  console.info('DEBUG: ChatApp mount', { selectedConvo, token });
  // Removed duplicate variable declarations

  // Fetch and cache dog owners for all dogIds in messages and adoptionDogId
  useEffect(() => {
    const ids = new Set<string>();
    if (adoptionDogId) ids.add(adoptionDogId);
    messages.forEach(m => { if (m.dogId) ids.add(m.dogId); });
    const fetchOwners = async () => {
      const newMap: Record<string, string> = {};
      await Promise.all(Array.from(ids).map(async id => {
        try {
          const res = await fetch(`${getApiUrl()}/api/dogs/${id}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          if (res.ok) {
            const dog = await res.json();
            newMap[id] = dog.owner;
          }
        } catch {}
      }));
      setDogOwnerMap(newMap);
    };
    if (token && ids.size > 0) fetchOwners();
  }, [adoptionDogId, messages, token]);
  // Removed duplicate variable declarations

  // Handler for sending a message
  const sendMessage = async () => {
    if (!input || !selectedConvo || !token) return;
    const recipient = selectedConvo.participants.find(id => id !== user._id);
    let messagePayload: any = {
      conversationId: selectedConvo._id,
      sender: user._id,
      recipient,
      message: input
    };
    // If adoptionDogId is set, send as adoption message
    if (adoptionDogId) {
      messagePayload.messageType = 'adoption';
      messagePayload.dogId = adoptionDogId;
      messagePayload.isOwner = adoptionIsOwner;
    }
    socketRef.current?.emit('sendMessage', messagePayload);
    await fetch(`${getApiUrl()}/api/chat/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(messagePayload)
    });
    // Optimistically add message to UI
    setMessages(prev => [
      ...prev,
      {
        _id: Math.random().toString(36).substr(2, 9),
        sender: user._id,
        recipient,
        message: input,
        sentAt: new Date().toISOString(),
        messageType: messagePayload.messageType,
        dogId: messagePayload.dogId,
        isOwner: messagePayload.isOwner
      }
    ]);
    setInput('');
    // Do not clear adoptionDogId or adoptionIsOwner here; keep them until adoption is confirmed or canceled
  };

  // Handler for input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    if (selectedConvo) {
      const recipient = selectedConvo.participants.find(id => id !== user._id);
      socketRef.current?.emit('typing', { recipient, sender: user._id });
    }
  };

  // Handler for deleting a conversation
  const handleDeleteConversation = async () => {
    if (!selectedConvo || !token) return;
    await fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    setSelectedConvo(null);
    setMessages([]);
  };

  // Block/unblock logic
    // Adoption handlers
    const handleConfirmAdoption = async (dogId: string, isOwner: boolean) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/confirm-adoption`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ dogId, isOwner })
        });
        if (res.ok) {
          setNotification(t('adoptionConfirmed'));
          setAdoptionDogId('');
          setAdoptionIsOwner(false);
          // UI will update via socket event (receiveMessage)
        }
      } catch (err) {
        setNotification(t('adoptionConfirmError'));
      }
    };
    const handleCancelAdoption = async (dogId: string) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/${dogId}/adopt-cancel`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ reason: '' })
        });
        if (res.ok) {
          setNotification(t('adoptionCanceled'));
          setAdoptionDogId('');
          setAdoptionIsOwner(false);
          // UI will update via socket event (receiveMessage)
        }
      } catch (err) {
        setNotification(t('adoptionCancelError'));
      }
    };
  const isBlocked = selectedConvo && userWithBlocks && userWithBlocks.blockedUsers && selectedConvo.participants.some(
    id => id !== userWithBlocks._id && userWithBlocks.blockedUsers!.includes(id)
  );
  const otherUserId = selectedConvo && userWithBlocks ? selectedConvo.participants.find(id => id !== userWithBlocks._id) : null;
  const handleBlockUnblock = async () => {
    if (!otherUserId || !token) return;
    const url = isBlocked ? '/api/chat/unblock' : '/api/chat/block';
    await fetch(`${getApiUrl()}${url}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
      body: JSON.stringify({ userId: user._id, blockId: otherUserId })
    });
  };
  useEffect(() => {
    if (user?._id && token) {
      // ...existing code...
      socketRef.current = io(getApiUrl());
      socketRef.current.on('connect', () => {
        // ...existing code...
      });
      socketRef.current.emit('join', user._id);
      socketRef.current.emit('getOnlineUsers');
      socketRef.current.on('onlineUsers', (users) => {
        setOnlineUsers(users.filter((u: any) => u._id !== user._id));
      });
      // Listen for adoption-related events to trigger in-app refresh
      socketRef.current.on('adoptionUpdate', () => {
        window.location.reload();
      });
      // Listen for dogUpdated event to refresh dog status/owner in chat UI
      socketRef.current.on('dogUpdated', ({ dog }) => {
        // Refetch status and owner for this dog
        if (dog && dog._id) {
          fetch(`${getApiUrl()}/api/dogs/${dog._id}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          })
            .then(res => res.json())
            .then(data => {
              setDogStatusMap(prev => ({ ...prev, [dog._id]: data.adoptionStatus }));
              setDogOwnerMap(prev => ({ ...prev, [dog._id]: data.owner }));
            });
        }
      });

      // Listen for receiveMessage event to update chat and adoption state in real time
      socketRef.current.on('receiveMessage', (msg) => {
        // If the message is an adoption completion/cancellation, show as system message and sync state
        const adoptionSystemKeywords = ['confirmed', 'completed', 'closed', 'canceled', 'cancelled'];
        const isAdoptionSystemMsg = msg.messageType === 'adoption' && msg.dogId && msg.message && adoptionSystemKeywords.some(k => msg.message.toLowerCase().includes(k));
        if (isAdoptionSystemMsg) {
          setMessages(prev => [...prev, {
            _id: Math.random().toString(36).substr(2, 9),
            sender: null,
            recipient: null,
            message: msg.message,
            sentAt: new Date().toISOString(),
            messageType: 'system',
            dogId: msg.dogId
          }]);
          setNotification(msg.message);
          // Refetch dog status and owner for this dog
          fetch(`${getApiUrl()}/api/dogs/${msg.dogId}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          })
            .then(res => res.json())
            .then(data => {
              setDogStatusMap(prev => ({ ...prev, [msg.dogId]: data.adoptionStatus }));
              setDogOwnerMap(prev => ({ ...prev, [msg.dogId]: data.owner }));
            });
          // Clear adoption UI state and hide action buttons
          setAdoptionDogId('');
          setAdoptionIsOwner(false);
        } else {
          setMessages(prev => [...prev, msg]);
          // If the message is an adoption message, update adoptionDogId and adoptionIsOwner
          if (msg.messageType === 'adoption' && msg.dogId) {
            setAdoptionDogId(msg.dogId);
            setAdoptionIsOwner(!!msg.isOwner);
          }
        }
      });
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, [user, token]);

  useEffect(() => {
    if (selectedConvo && token) {
      fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(async data => {
          setMessages(data);
          // Always check for adoption message and refresh dog status
          let found = false;
          const adoptionMsg = data.find((m: any) => m.messageType === 'adoption' && m.dogId);
          if (adoptionMsg && adoptionMsg.dogId) {
            setAdoptionDogId(adoptionMsg.dogId);
            setAdoptionIsOwner(adoptionMsg.isOwner || false);
            found = true;
            // Refetch dog status for this dog
            fetch(`${getApiUrl()}/api/dogs/${adoptionMsg.dogId}`, {
              headers: { 'Authorization': `Bearer ${token}` }
            })
              .then(res => res.json())
              .then(dog => {
                setDogStatusMap(prev => ({ ...prev, [adoptionMsg.dogId]: dog.adoptionStatus }));
                setDogOwnerMap(prev => ({ ...prev, [adoptionMsg.dogId]: dog.owner }));
                // If adoption is completed/canceled, show system message and clear adoption UI
                if (dog.adoptionStatus === 'completed' || dog.adoptionStatus === 'closed' || dog.adoptionStatus === 'canceled' || dog.adoptionStatus === 'cancelled') {
                  setNotification(t('adoptionConfirmed'));
                  setAdoptionDogId('');
                  setAdoptionIsOwner(false);
                  setMessages(prev => [
                    ...prev,
                    {
                      _id: Math.random().toString(36).substr(2, 9),
                      sender: null,
                      recipient: null,
                      message: t('adoptionConfirmed'),
                      sentAt: new Date().toISOString(),
                      messageType: 'system',
                      dogId: adoptionMsg.dogId
                    }
                  ]);
                }
              });
          }
          // If not found, check all dogs for pending adoptions involving these users
          if (!found) {
            try {
              const res = await fetch(`${getApiUrl()}/api/dogs`, { headers: { 'Authorization': `Bearer ${token}` } });
              if (res.ok) {
                const dogs = await res.json();
                const convoUserIds = selectedConvo.participants.map(String);
                const pendingDog = dogs.find((dog: any) =>
                  dog.adoptionStatus === 'pending' &&
                  dog.adoptionQueue && dog.adoptionQueue.adopter &&
                  convoUserIds.includes(String(dog.user._id || dog.user)) &&
                  convoUserIds.includes(String(dog.adoptionQueue.adopter))
                );
                if (pendingDog && pendingDog._id) {
                  setAdoptionDogId(pendingDog._id);
                  setAdoptionIsOwner(String(pendingDog.user._id || pendingDog.user) === String(user._id));
                  // Refetch dog status for this dog
                  fetch(`${getApiUrl()}/api/dogs/${pendingDog._id}`, {
                    headers: { 'Authorization': `Bearer ${token}` }
                  })
                    .then(res => res.json())
                    .then(dog => {
                      setDogStatusMap(prev => ({ ...prev, [pendingDog._id]: dog.adoptionStatus }));
                      setDogOwnerMap(prev => ({ ...prev, [pendingDog._id]: dog.owner }));
                    });
                }
              }
            } catch {}
          }
        });
    }

    // Listen for open-chat-with-user event to start conversation with given userId
    const chatHandler = (e: any) => {
      if (e && e.detail && e.detail.userId) {
        const targetId = e.detail.userId;
        // Find or create a conversation with this user
        let convo = null;
        for (const c of onlineUsers) {
          if (typeof c === 'object' && c._id === targetId) {
            convo = { _id: c._id, participants: [user._id, c._id] };
            break;
          }
        }
        if (convo) {
          setSelectedConvo(convo);
        } else {
          // Fallback: create a new conversation object
          setSelectedConvo({ _id: targetId, participants: [user._id, targetId] });
        }
        // Force chat UI to open (for mobile)
        setShowChat(true);
      }
    };
    window.addEventListener('open-chat-with-user', chatHandler);
    return () => {
      window.removeEventListener('open-chat-with-user', chatHandler);
    };
  }, [selectedConvo, token, user._id, onlineUsers, user]);

  // Fetch adoption status for all dogIds in messages and adoptionDogId
  // Always fetch status for adoptionDogId when it changes, even if messages don't change
  useEffect(() => {
    if (!adoptionDogId || !token) return;
    const fetchStatus = async () => {
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/${adoptionDogId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        if (res.ok) {
          const dog = await res.json();
          setDogStatusMap(prev => ({ ...prev, [adoptionDogId]: dog.adoptionStatus }));
        }
      } catch {}
    };
    fetchStatus();
  }, [adoptionDogId, token]);

  // Also fetch status for all dogIds in messages (for chat history)
  useEffect(() => {
    if (!token) return;
    const ids = new Set<string>();
    messages.forEach(m => { if (m.dogId) ids.add(m.dogId); });
    if (ids.size === 0) return;
    const fetchStatuses = async () => {
      const newMap: Record<string, string> = {};
      await Promise.all(Array.from(ids).map(async id => {
        try {
          const res = await fetch(`${getApiUrl()}/api/dogs/${id}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          if (res.ok) {
            const dog = await res.json();
            newMap[id] = dog.adoptionStatus;
          }
        } catch {}
      }));
      setDogStatusMap(prev => ({ ...prev, ...newMap }));
    };
    fetchStatuses();
  }, [messages, token]);

  const startConversation = async (otherUserId: string) => {
    if (!otherUserId || !token) return;
    // Always create or fetch the conversation
    let convo;
    try {
      const res = await fetch(`${getApiUrl()}/api/chat/conversation`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ userId: user._id, otherUserId })
      });
      convo = await res.json();
    } catch {
      convo = null;
    }
    // Fallback: if convo is invalid, create minimal object
    if (!convo || !convo._id || !convo.participants) {
      convo = { _id: otherUserId, participants: [user._id, otherUserId] };
    }
    setSelectedConvo(convo);
    // Fetch messages for this conversation
    fetch(`${getApiUrl()}/api/chat/messages/${convo._id}`, {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(async data => {
        setMessages(data);
        // Check for pending adoption in this chat
        let found = false;
        const adoptionMsg = data.find((m: any) => m.messageType === 'adoption' && m.dogId);
        if (adoptionMsg && adoptionMsg.dogId) {
          setAdoptionDogId(adoptionMsg.dogId);
          setAdoptionIsOwner(adoptionMsg.isOwner || false);
          found = true;
        }
        if (!found) {
          try {
            const res = await fetch(`${getApiUrl()}/api/dogs`, { headers: { 'Authorization': `Bearer ${token}` } });
            if (res.ok) {
              const dogs = await res.json();
              const convoUserIds = convo.participants.map(String);
              const pendingDog = dogs.find((dog: any) =>
                dog.adoptionStatus === 'pending' &&
                dog.adoptionQueue && dog.adoptionQueue.adopter &&
                convoUserIds.includes(String(dog.user._id || dog.user)) &&
                convoUserIds.includes(String(dog.adoptionQueue.adopter))
              );
              if (pendingDog && pendingDog._id) {
                setAdoptionDogId(pendingDog._id);
                setAdoptionIsOwner(String(pendingDog.user._id || pendingDog.user) === String(user._id));
              }
            }
          } catch {}
        }
      });
  };

  return (
    <div className="chat-app-container" style={{position: 'fixed', right: '60px', bottom: '32px', display: 'flex', flexDirection: 'column', width: '480px', maxWidth: '100vw', height: '80vh', zIndex: 1002}}>
      <button
        className="chat-app-close-btn"
        onClick={() => window.dispatchEvent(new CustomEvent('closeChatModal'))}
        title="Close"
        aria-label="Close"
        type="button"
      >
        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" style={{display:'block',margin:'auto'}}>
          <circle cx="8" cy="8" r="7" fill="#e74c3c"/>
          <path d="M4.5 4.5L11.5 11.5M11.5 4.5L4.5 11.5" stroke="#fff" strokeWidth="2" strokeLinecap="round"/>
        </svg>
      </button>
      <div style={{display: 'flex', flex: 1, minHeight: 0}}>
        <div className="chat-app-sidebar" style={{width: '220px', minWidth: 0, maxWidth: '100%', borderRight: '1px solid #e3e3e3', borderBottom: 'none'}}>
          <div className="chat-app-online-users">
            <b style={{ fontSize: 13, color: '#43b581' }}>Online Users</b>
            <ul style={{ listStyle: 'none', padding: 0, minHeight: 40 }}>
              {onlineUsers.filter(u => {
                const id = typeof u === 'string' ? u : u._id;
                return String(id) !== String(user._id);
              }).length === 0 && (
                <li style={{ color: '#aaa', fontSize: 12 }}>{t('noUsersOnline')}</li>
              )}
              {onlineUsers.filter(u => {
                const id = typeof u === 'string' ? u : u._id;
                return String(id) !== String(user._id);
              }).map(u => {
                let displayName = '';
                let profileImg = '../img/androcolored-80x80.jpg';
                if (typeof u === 'string') {
                  displayName = u;
                } else if (u && typeof u === 'object') {
                  displayName = u.userName || u._id || 'Unknown';
                  if (u.profilePicture) {
                    profileImg = `${getApiUrl()}${u.profilePicture}`;
                  }
                }
                return (
                  <li key={typeof u === 'string' ? u : u._id} className="chat-app-user-row">
                    <span className="chat-app-online-dot" style={{ position: 'absolute', left: 8, top: 18 }} />
                    <img
                      src={profileImg}
                      alt={displayName}
                      style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #ddd', minWidth: '36px', minHeight: '36px', maxWidth: '40px', maxHeight: '40px', marginRight: 8 }}
                      onError={e => { (e.target as HTMLImageElement).src = '../img/androcolored-80x80.jpg'; }}
                    />
                    <button className="chat-app-convo-btn" style={{ fontSize: 13, fontWeight: 500, margin: 0, padding: 0, background: 'none', border: 'none', color: '#1f4b72', cursor: 'pointer' }} onClick={() => startConversation(typeof u === 'string' ? u : u._id)}>{displayName}</button>
                  </li>
                );
              })}
            </ul>
          </div>
          <input
            className="chat-app-search"
            type="text"
            value={userSearch}
            onChange={e => setUserSearch(e.target.value)}
            placeholder={t('searchUsersPlaceholder')}
          />
        </div>
        <div className="chat-app-main" style={{flex: 1, minWidth: 0, minHeight: 0, display: 'flex', flexDirection: 'column', background: 'none', boxShadow: 'none'}}>
          {selectedConvo && (
            <>
              {[
                <div className="chat-app-actions-row" key="actions-row">
                  <button className="chat-app-action-btn delete" onClick={handleDeleteConversation}>{t('deleteConversation')}</button>
                  <button className={`chat-app-action-btn ${isBlocked ? 'unblock' : 'block'}`} onClick={handleBlockUnblock}>{isBlocked ? t('unblock') : t('block')}</button>
                </div>,
                <div className="chat-app-messages" key="messages" style={{
                  flex: 1,
                  minHeight: 0,
                  maxHeight: '100%',
                  overflowY: 'auto',
                  display: 'flex',
                  flexDirection: 'column',
                  background: '#f5f8fa',
                  border: '1.5px solid #e3e3e3',
                  borderRadius: 10,
                  marginBottom: 10,
                  boxShadow: '0 2px 8px rgba(33,150,243,0.06)'
                }}>
                  {messages.map(msg => (
                    <div
                      key={msg._id}
                      className={`chat-app-message${user && msg.sender === user._id ? ' self' : ''}${msg.sender === null ? ' system' : ''}`}
                      style={msg.sender === null ? { textAlign: 'center', color: '#888', fontStyle: 'italic', background: '#f9f9f9', border: 'none', margin: '8px 0' } : {}}
                    >
                      <span className={`chat-app-bubble${user && msg.sender === user._id ? ' self' : ''}`}>{msg.message}</span>
                    </div>
                  ))}
                  {typing && <div className="chat-app-typing">{t('typing')}</div>}
                </div>,
                adoptionDogId && selectedConvo && dogStatusMap[adoptionDogId] !== 'pending' && !messages.some(m => m.messageType === 'adoption' && m.dogId === adoptionDogId) && (
                  <div style={{margin:'8px 0'}} key="adopt-btn">
                    <button
                      className="chat-app-action-btn confirm"
                      onClick={() => sendAdoptionMessage(adoptionDogId)}
                      style={{fontSize:14, padding:'6px 18px', background:'#267822', color:'#fff', borderRadius:6, border:'none'}}
                    >
                      {t('adopt') || 'Adopt'}
                    </button>
                  </div>
                ),
                (() => { try { console.log("DEBUG:", {adoptionDogId, dogStatus: dogStatusMap[adoptionDogId], selectedConvo, user}); } catch(e) {} return null; })(),
                adoptionDogId && selectedConvo && dogStatusMap[adoptionDogId] === 'pending' && (() => {
                  const dogOwner = dogOwnerMap[adoptionDogId];
                  if (!dogOwner || dogOwner !== user._id) {
                    return (
                      <div className="chat-app-message" key="adoption-actions">
                        <span className="chat-app-bubble">{t('adoptionRequest')}</span>
                        <div className="chat-app-adoption-actions">
                          <button className="chat-app-action-btn confirm" onClick={() => handleConfirmAdoption(adoptionDogId, false)}>{t('confirmAdoption')}</button>
                          <button className="chat-app-action-btn cancel" onClick={() => handleCancelAdoption(adoptionDogId)}>{t('cancelAdoption')}</button>
                        </div>
                      </div>
                    );
                  }
                  return null;
                })()
              ].filter(Boolean)}
            </>
          )}
          {selectedConvo && (
            <div className="chat-app-input-row">
              <input
                className="chat-app-input"
                type="text"
                value={input}
                onChange={handleInputChange}
                onKeyDown={e => { if (e.key === 'Enter') sendMessage(); }}
                placeholder={t('typeMessagePlaceholder') || 'Type a message...'}
              />
              <button
                className="chat-app-send-btn"
                onClick={sendMessage}
                disabled={!input.trim()}
              >{t('send') || 'Send'}</button>
            </div>
          )}
          {notification && (
            <div className="chat-app-notification">{notification}</div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ChatApp;
