import React, { useEffect, useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';

interface EditDogFormData {
  name: string;
  breed?: string;
  age?: number;
  color?: string;
  description?: string;
  size?: string;
  gender?: 'male' | 'female';
  vaccinated?: boolean;
  neutered?: boolean;
  images?: string[];
}

interface EditDogModalProps {
  dog: any;
  isSingleDog: boolean;
  onSave: (dog: any) => void;
}

const EditDogModal: React.FC<EditDogModalProps> = ({ dog, isSingleDog, onSave }) => {
  const { t } = useTranslation();
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });
    const { register, handleSubmit } = useForm<EditDogFormData>({
      defaultValues: {
        ...dog,
      }
    });
  useEffect(() => {
    const handleResize = () => setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    console.log('[EditDogModal] Mounted with dog:', dog);
  }, [dog]);

  const onSubmit: SubmitHandler<EditDogFormData> = fields => {
    // Convert string values to correct types for health fields
    const updatedFields = {
      ...fields,
      vaccinated: String(fields.vaccinated) === 'true',
      neutered: String(fields.neutered) === 'true',
      gender: fields.gender === 'male' ? 'male' : 'female',
    };
    console.log('[EditDogModal] onSubmit called with fields:', updatedFields);
    onSave({ ...dog, ...updatedFields });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      background: 'rgba(0,0,255,0.2)',
      zIndex: 999999,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      pointerEvents: 'auto',
    }}>
      <div style={{ position: 'relative', minWidth: '320px', minHeight: '220px', maxWidth: '90vw', maxHeight: '90vh' }}>
        <button
          type="button"
          onClick={() => {
            if (typeof window !== 'undefined' && window.dispatchEvent) {
              window.dispatchEvent(new CustomEvent('closeEditDogModal'));
            }
          }}
          style={{
            position: 'absolute',
            top: 12,
            right: 12,
            width: 36,
            height: 36,
            background: '#e74c3c',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            fontSize: '1.5rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            zIndex: 1000000,
          }}
          aria-label="Close"
          title="Close"
        >
          Ã—
        </button>
        <form
          onSubmit={handleSubmit(onSubmit)}
          style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '1.2rem',
            fontSize: '1rem',
            background: 'white',
            border: '2px solid #3498db',
            borderRadius: 12,
            padding: '2rem',
            minWidth: '320px',
            minHeight: '220px',
            maxWidth: '90vw',
            maxHeight: '90vh',
            zIndex: 9999999,
            boxSizing: 'border-box',
            overflow: 'auto',
          }}
        >
          <label>{t('adddog.name')}</label>
          <input
            {...register('name')}
            defaultValue={dog.name || ''}
            required
            style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
          />
          <label>{t('adddog.breed')}</label>
          <input
            {...register('breed')}
            defaultValue={dog.breed || ''}
            style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
          />
          <label>{t('adddog.age') || 'Age'}</label>
          <input type="number" {...register('age')} defaultValue={dog.age || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.color') || 'Color'}</label>
          <input {...register('color')} defaultValue={dog.color || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.description') || 'Description'}</label>
          <textarea {...register('description')} defaultValue={dog.description || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.size') || 'Size'}</label>
          <input {...register('size')} defaultValue={dog.size || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.gender') || 'Gender'}</label>
          <div style={{ display: 'flex', gap: '2rem', marginBottom: '1rem' }}>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', fontWeight: 'bold', fontSize: '1.1rem' }}>
                <input type="radio" id="gender-male" value="male" {...register('gender')} defaultChecked={dog.gender === 'male'} style={{ accentColor: '#3498db', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #3498db' }} />
                Male
              </label>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', fontWeight: 'bold', fontSize: '1.1rem' }}>
                <input type="radio" id="gender-female" value="female" {...register('gender')} defaultChecked={dog.gender === 'female'} style={{ accentColor: '#e74c3c', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #e74c3c' }} />
                Female
              </label>
            </div>
          </div>
          {/* Health radio buttons */}
          <div style={{ display: 'flex', gap: '2rem', marginBottom: '1rem' }}>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              <span style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: '0.5rem' }}>Vaccinated:</span>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input type="radio" id="vaccinated-yes" value="true" {...register('vaccinated')} defaultChecked={!!dog.vaccinated} style={{ accentColor: '#27ae60', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #27ae60' }} />
                Yes
              </label>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input type="radio" id="vaccinated-no" value="false" {...register('vaccinated')} defaultChecked={!dog.vaccinated} style={{ accentColor: '#e74c3c', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #e74c3c' }} />
                No
              </label>
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              <span style={{ fontWeight: 'bold', fontSize: '1.1rem', marginBottom: '0.5rem' }}>Neutered:</span>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input type="radio" id="neutered-yes" value="true" {...register('neutered')} defaultChecked={!!dog.neutered} style={{ accentColor: '#27ae60', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #27ae60' }} />
                Yes
              </label>
              <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <input type="radio" id="neutered-no" value="false" {...register('neutered')} defaultChecked={!dog.neutered} style={{ accentColor: '#e74c3c', width: 20, height: 20, marginRight: 6, background: '#fff', border: '2px solid #e74c3c' }} />
                No
              </label>
            </div>
          </div>
          {/* Image upload */}
          <label style={{ fontWeight: 'bold', fontSize: '1.1rem' }}>Images</label>
          <input type="file" multiple accept="image/*" {...register('images')} style={{ marginBottom: '1rem' }} />
          <div style={{
            display: 'flex',
            justifyContent: 'flex-end',
            marginTop: '1.5rem',
            position: 'sticky',
            bottom: 0,
            background: 'white',
            zIndex: 10,
            paddingBottom: 1
          }}>
            <button
              type="submit"
              style={{
                background: '#3498db',
                color: '#fff',
                border: 'none',
                borderRadius: 6,
                padding: '0.5rem 1.2rem',
                fontWeight: 'bold',
                fontSize: '1rem',
                cursor: 'pointer',
                boxShadow: '0 2px 8px rgba(52,152,219,0.08)'
              }}
            >
              {t('editdog.save').replace('editdog.', '').replace('adddog.', '') || 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditDogModal;
