import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './dogImageSliderFix.css';

interface DogImageSliderProps {
  images: { url: string; width?: number }[];
  alt?: string;
}

// Deduplicate images by base name (same logic as EditDogModal)
const getImageBase = (url: string) => {
  let cleanUrl = url.split('?')[0];
  const filename = cleanUrl.substring(cleanUrl.lastIndexOf('/') + 1);

  let base = filename;

  // Remove file extension
  base = base.replace(/\.(jpg|jpeg|png|webp)$/i, '');

  // Handle uploaded files: name-TIMESTAMP-HASH-SIZE -> name
  if (base.includes('-') && !base.startsWith('pexels-')) {
    base = base.replace(/-orig$/, '');
    const uploadedMatch = base.match(/^(.+?)-\d{10,13}-[a-z0-9]{6}-\d{3,4}$/);
    if (uploadedMatch) {
      base = uploadedMatch[1];
    }
  } else if (base.startsWith('pexels-')) {
    // Handle Pexels images: pexels-name-ID-TIMESTAMP-HASH-SIZE -> name-ID
    base = base.replace(/-orig$/, '');
    const pexelsMatch = base.match(/^pexels-(.+?)-\d+(-\d{10,13}-[a-z0-9]{6}-\d{3,4})?$/);
    if (pexelsMatch) {
      base = pexelsMatch[1];
    }
  }

  return base;
};

const DogImageSlider: React.FC<DogImageSliderProps> = ({ images, alt }) => {
  if (!images || images.length === 0) return null;

  // Deduplicate images - keep one with largest width per base
  const baseToImage = new Map();
  images.forEach((img) => {
    if (!img.url) return;
    const base = getImageBase(img.url);
    if (!baseToImage.has(base)) {
      baseToImage.set(base, img);
    } else {
      const existing = baseToImage.get(base);
      if ((img.width || 0) > (existing.width || 0)) {
        baseToImage.set(base, img);
      }
    }
  });
  const uniqueImages = Array.from(baseToImage.values());

  // Responsive maxHeight: 480px on desktop, 320px on mobile
  const isMobile = typeof window !== 'undefined' && window.innerWidth <= 768;
  const maxHeight = isMobile ? 320 : 480;

  return (
    <div className="dog-image-slider">
      <Swiper
        modules={[Navigation, Pagination]}
        spaceBetween={10}
        slidesPerView={1}
        loop={images.length > 1}
        pagination={{ clickable: true }}
        navigation={true}
        style={{ borderRadius: '1rem', maxHeight }}
      >
        {images.map((img, idx) => (
          <SwiperSlide key={img.url}>
            <img
              src={img.url}
              alt={alt || `dog-image-${idx + 1}`}
              style={{ width: '100%', height: 'auto', borderRadius: '1rem', objectFit: 'contain', background: '#111', maxHeight }}
              onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = '/img/nany.jpg'; }}
            />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default DogImageSlider;
