import React, { useEffect, useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';

interface EditDogFormData {
  name: string;
  breed?: string;
}

interface EditDogModalProps {
  dog: any;
  isSingleDog: boolean;
  onSave: (dog: any) => void;
}

const EditDogModal: React.FC<EditDogModalProps> = ({ dog, isSingleDog, onSave }) => {
  const { t } = useTranslation();
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });
  const { register, handleSubmit } = useForm<EditDogFormData>({ defaultValues: dog });
  useEffect(() => {
    const handleResize = () => setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const onSubmit: SubmitHandler<EditDogFormData> = fields => {
    onSave({ ...dog, ...fields });
  };

  // Small modal style for desktop
  const modalStyle = (isSingleDog && windowSize.width > 768)
    ? {
        maxWidth: '320px',
        margin: '40px auto',
        padding: '0.5rem 0.5rem',
        background: '#fff',
        borderRadius: 10,
        boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
        fontSize: '0.8rem',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }
    : {};

  return (
    <div style={modalStyle}>
      <form
        onSubmit={handleSubmit(onSubmit)}
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: isSingleDog ? '0.2rem' : '1rem',
          fontSize: isSingleDog ? '0.8rem' : undefined,
          width: '100%',
        }}
      >
        {(isSingleDog && windowSize.width > 768) ? (
          <>
            <label style={{ fontSize: '0.8rem', marginBottom: 2 }}>{t('adddog.name')}</label>
            <input
              {...register('name')}
              defaultValue={dog.name || ''}
              required
              style={{ padding: '0.15rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '0.8rem', width: '100%' }}
            />
            <label style={{ fontSize: '0.8rem', marginBottom: 2 }}>{t('adddog.breed')}</label>
            <input
              {...register('breed')}
              defaultValue={dog.breed || ''}
              style={{ padding: '0.15rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '0.8rem', width: '100%' }}
            />
            <div style={{
              display: 'flex',
              justifyContent: 'flex-end',
              marginTop: 8,
              width: '100%',
            }}>
              <button
                type="submit"
                style={{
                  background: '#3498db',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 6,
                  padding: '0.18rem 0.5rem',
                  fontWeight: 'bold',
                  fontSize: '0.8rem',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(52,152,219,0.08)'
                }}
              >
                {t('editdog.save').replace('editdog.', '').replace('adddog.', '') || 'Save'}
              </button>
            </div>
          </>
        ) : null}
      </form>
    </div>
  );
};

export default EditDogModal;
