import React, { useEffect, useState } from 'react';
import CardSmall from '../CardSmall';
import Search from '../Search';
import { useTranslation } from 'react-i18next';
import EditDogModal from './EditDogModal';
import RemoveDogModal from './RemoveDogModal';

interface DogType {
	_id: string;
	name: string;
	breed?: string;
	age?: number;
	gender?: string;
	place?: string;
	video?: any;
	thumbnail?: any;
	canEdit?: boolean;
	vaccinated?: boolean;
	images?: { url: string; width?: number }[];
	adoptionStatus?: string;
}


const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';


function DogList() {
	const { t } = useTranslation();
	const [dogs, setDogs] = useState<DogType[]>([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState<string | null>(null);
	const [editDog, setEditDog] = useState<DogType | null>(null);
	const [removeDog, setRemoveDog] = useState<DogType | null>(null);
	const [locationDog, setLocationDog] = useState<DogType | null>(null);
	const [locationCoords, setLocationCoords] = useState<{lat: number, lon: number} | null>(null);
	const [locationLoading, setLocationLoading] = useState(false);

	useEffect(() => {
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			})
			.catch(err => {
				setError('Failed to load dogs');
				setLoading(false);
			});
	}, []);

	const handleEdit = (dog: DogType) => setEditDog(dog);
	const handleRemove = (dog: DogType) => setRemoveDog(dog);
		const handleLocation = async (dog: DogType) => {
			setLocationDog(dog);
			setLocationCoords(null);
			if (dog.place) {
				setLocationLoading(true);
				try {
					const res = await fetch(`https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(dog.place)}&format=json&limit=1`);
					const data = await res.json();
					if (data && data.length > 0) {
						setLocationCoords({ lat: parseFloat(data[0].lat), lon: parseFloat(data[0].lon) });
					}
				} catch {}
				setLocationLoading(false);
			}
		};

	const handleEditSave = async (updatedDog: DogType) => {
		// PATCH to API
		await fetch(`${API_URL}/api/dogs/${updatedDog._id}`, {
			method: 'PATCH',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(updatedDog),
		});
		setEditDog(null);
		// Refresh list
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			});
	};

	const handleRemoveConfirm = async () => {
		if (!removeDog) return;
		await fetch(`${API_URL}/api/dogs/${removeDog._id}`, { method: 'DELETE' });
		setRemoveDog(null);
		// Refresh list
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			});
	};

	if (loading) return <div>{t('doglist.loading') || 'Loading dogs...'}</div>;
	if (error) return <div style={{ color: 'red' }}>{error}</div>;

	return (
		<>
			<div id="Doglist">
				{dogs.length === 0 ? (
					<div>{t('doglist.empty') || 'No dogs found.'}</div>
				) : (
					dogs.map(dog => (
						<CardSmall
							key={dog._id}
							{...dog}
							canEdit={true}
							onEdit={() => handleEdit(dog)}
							onRemove={() => handleRemove(dog)}
							onViewDetails={() => handleLocation(dog)}
						/>
					))
				)}
			</div>
			{editDog && (
				<EditDogModal
					dog={editDog}
					isSingleDog={true}
					onSave={handleEditSave}
					onClose={() => setEditDog(null)}
				/>
			)}
			{removeDog && (
				<RemoveDogModal
					dog={removeDog}
					onConfirm={handleRemoveConfirm}
					onClose={() => setRemoveDog(null)}
				/>
			)}
			{/* Location modal: show map for dog.place if needed */}
						{locationDog && locationDog.place && (
							<div style={{
								position: 'fixed',
								top: '50%',
								left: '50%',
								transform: 'translate(-50%, -50%)',
								background: '#fff',
								border: '2px solid #3498db',
								borderRadius: 16,
								zIndex: 2147483647,
								minWidth: 320,
								maxWidth: 640,
								padding: 16,
								width: '90vw',
								maxHeight: '90vh',
								boxSizing: 'border-box',
								overflow: 'auto',
							}}>
								<button
									type="button"
									onClick={() => setLocationDog(null)}
									style={{
										position: 'absolute',
										top: 12,
										right: 12,
										width: 40,
										height: 40,
										background: '#e74c3c',
										color: '#fff',
										border: 'none',
										borderRadius: '50%',
										fontSize: '1.5rem',
										fontWeight: 'bold',
										cursor: 'pointer',
										zIndex: 2,
									}}
									aria-label="Close"
									title="Close"
								>
									×
								</button>
								{locationLoading && <div>Loading map…</div>}
								{locationCoords ? (
									<iframe
										title="Map"
										width="100%"
										height="250"
										style={{ borderRadius: 12, border: '1px solid #ccc', marginTop: 16, zIndex: 1 }}
										src={`https://www.openstreetmap.org/export/embed.html?bbox=${locationCoords.lon-0.01},${locationCoords.lat-0.01},${locationCoords.lon+0.01},${locationCoords.lat+0.01}&layer=mapnik&marker=${locationCoords.lat},${locationCoords.lon}`}
										allowFullScreen
									/>
								) : !locationLoading && (
									<div style={{marginTop:16, color:'#888'}}>Location not found</div>
								)}
								<div style={{ fontSize: '0.9rem', marginTop: 4 }}>
									<a
										href={locationCoords ?
											`https://www.openstreetmap.org/?mlat=${locationCoords.lat}&mlon=${locationCoords.lon}#map=16/${locationCoords.lat}/${locationCoords.lon}` :
											`https://www.openstreetmap.org/search?query=${encodeURIComponent(locationDog.place)}`}
										target="_blank"
										rel="noopener noreferrer"
									>
										View larger map
									</a>
								</div>
							</div>
						)}
		</>
	);
}

export default DogList;
