import React, { useEffect, useState } from 'react';
import CardSmall from '../CardSmall';
import { useTranslation } from 'react-i18next';
import EditDogModal from './EditDogModal';
import RemoveDogModal from './RemoveDogModal';
import DogDetails from './DogDetails';

interface DogType {
	_id: string;
	name: string;
	breed?: string;
	age?: number;
	gender?: string;
	place?: string;
	location?: string;
	video?: any;
	thumbnail?: any;
	canEdit?: boolean;
	vaccinated?: boolean;
	images?: { url: string; width?: number }[];
	adoptionStatus?: string;
	user?: {
		_id: string;
		name: string;
		username?: string;
		email?: string;
		phone?: string;
		person?: 'private' | 'organization';
	};
}


const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';


function DogList() {
	const { t } = useTranslation();
	const [dogs, setDogs] = useState<DogType[]>([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState<string | null>(null);
	const [editDog, setEditDog] = useState<DogType | null>(null);
	const [removeDog, setRemoveDog] = useState<DogType | null>(null);
	const [detailsDog, setDetailsDog] = useState<DogType | null>(null);

	useEffect(() => {
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			})
			.catch(err => {
				setError('Failed to load dogs');
				setLoading(false);
			});
	}, []);

		const handleEdit = (dog: DogType) => setEditDog(dog);
		const handleRemove = (dog: DogType) => setRemoveDog(dog);
		const handleDetails = (dog: DogType) => setDetailsDog(dog);

	const handleEditSave = async (updatedDog: DogType) => {
		// PATCH to API
		await fetch(`${API_URL}/api/dogs/${updatedDog._id}`, {
			method: 'PATCH',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(updatedDog),
		});
		setEditDog(null);
		// Refresh list
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			});
	};

	const handleRemoveConfirm = async () => {
		if (!removeDog) return;
		await fetch(`${API_URL}/api/dogs/${removeDog._id}`, { method: 'DELETE' });
		setRemoveDog(null);
		// Refresh list
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			});
	};

	if (loading) return <div>{t('doglist.loading') || 'Loading dogs...'}</div>;
	if (error) return <div style={{ color: 'red' }}>{error}</div>;

		       // Responsive: side-by-side on desktop, modal on mobile
		       const isDesktop = typeof window !== 'undefined' && window.innerWidth >= 900;

		       return (
			       <div style={{
				       display: isDesktop ? 'flex' : 'block',
				       flexDirection: isDesktop ? 'row' : undefined,
				       alignItems: isDesktop ? 'flex-start' : undefined,
				       justifyContent: isDesktop ? 'center' : undefined,
				       gap: isDesktop ? 32 : undefined,
				       position: 'relative',
			       }}>
								       <div id="Doglist" style={{
									       flex: isDesktop ? '0 0 auto' : undefined,
									       margin: isDesktop ? '32px 0 32px 32px' : undefined,
									       overflowY: isDesktop ? 'auto' : undefined,
									       maxHeight: isDesktop ? '100vh' : undefined,
									       width: isDesktop ? undefined : '100vw',
									       overflowX: isDesktop ? undefined : 'auto',
									       WebkitOverflowScrolling: isDesktop ? undefined : 'touch',
								       }}>
					       {dogs.length === 0 ? (
						       <div>{t('doglist.empty') || 'No dogs found.'}</div>
					       ) : (
							   <div style={{
								   display: isDesktop ? 'flex' : 'flex',
								   flexDirection: isDesktop ? 'row' : 'row',
								   gap: isDesktop ? 16 : 12,
								   flexWrap: isDesktop ? 'wrap' : 'nowrap',
								   alignItems: isDesktop ? 'flex-start' : 'stretch',
								   overflowX: isDesktop ? undefined : 'auto',
								   width: isDesktop ? undefined : '100vw',
							   }}>
							       {dogs.map(dog => (
								       <div
									       key={dog._id}
									       style={isDesktop ? {
										       background: '#fafbfc',
										       borderRadius: 16,
										       boxShadow: '0 2px 16px rgba(0,0,0,0.06)',
										       padding: 16,
										       display: 'block',
											   minWidth: 280,
											   maxWidth: 400,
										       width: 'auto',
										       verticalAlign: 'top',
									       } : {}}
								       >
										   <CardSmall
												   {...dog}
												   canEdit={true}
												   onEdit={() => handleEdit(dog)}
												   onRemove={() => handleRemove(dog)}
												   onViewDetails={(e) => {
													   // If called from location span, show map immediately
													   if (e && Object.keys(e).length === 0) {
														   handleDetails({ ...dog, _showMap: true } as any);
													   } else {
														   handleDetails(dog);
													   }
												   }}
										   />
								       </div>
							       ))}
							</div>
					       )}
					</div>
				       {editDog && (
					       <EditDogModal
						       dog={editDog}
						       isSingleDog={true}
						       onSave={handleEditSave}
						       onClose={() => setEditDog(null)}
					       />
				       )}
				       {removeDog && (
					       <RemoveDogModal
						       dog={removeDog}
						       onConfirm={handleRemoveConfirm}
						       onClose={() => setRemoveDog(null)}
					       />
				       )}
					       {/* Details panel on desktop, modal on mobile */}
							       {detailsDog && (
								       isDesktop ? (
									       <div style={{
										       position: 'fixed',
										       top: 0,
										       left: 0,
										       width: '100vw',
										       height: '100vh',
										       background: 'rgba(0,0,0,0.5)',
										       zIndex: 2147483647,
										       display: 'flex',
										       alignItems: 'center',
										       justifyContent: 'center',
									       }}>
										       <div style={{
											       background: '#fff',
											       borderRadius: 16,
											       maxWidth: 700,
											       width: '95vw',
											       maxHeight: '95vh',
											       overflow: 'auto',
											       position: 'relative',
											       padding: 24,
										       }}>
											       <button onClick={()=>setDetailsDog(null)} style={{position:'absolute',top:12,right:12,width:40,height:40,background:'#e74c3c',color:'#fff',border:'none',borderRadius:'50%',fontSize:'1.5rem',fontWeight:'bold',cursor:'pointer',zIndex:2}} aria-label="Close" title="Close">×</button>
											   <DogDetails
												   {...detailsDog}
												   gender={detailsDog.gender === 'male' || detailsDog.gender === 'female' ? detailsDog.gender : undefined}
												   user={detailsDog.user || null}
												   place={detailsDog.place}
												   location={detailsDog.location || detailsDog.place}
												   onClose={()=>setDetailsDog(null)}
											   />
										       </div>
									       </div>
								       ) : (
									       <div style={{position:'fixed',top:0,left:0,width:'100vw',height:'100vh',background:'rgba(0,0,0,0.5)',zIndex:2147483647,display:'flex',alignItems:'center',justifyContent:'center'}}>
										       <div style={{background:'#fff',borderRadius:16,maxWidth:700,width:'95vw',maxHeight:'95vh',overflow:'auto',position:'relative',padding:24}}>
											       <button onClick={()=>setDetailsDog(null)} style={{position:'absolute',top:12,right:12,width:40,height:40,background:'#e74c3c',color:'#fff',border:'none',borderRadius:'50%',fontSize:'1.5rem',fontWeight:'bold',cursor:'pointer',zIndex:2}} aria-label="Close" title="Close">×</button>
											       <DogDetails
												       {...detailsDog}
												       gender={detailsDog.gender === 'male' || detailsDog.gender === 'female' ? detailsDog.gender : undefined}
												       user={detailsDog.user || null}
												       place={detailsDog.place}
												       location={detailsDog.location || detailsDog.place}
												       onClose={()=>setDetailsDog(null)}
											       />
										       </div>
									       </div>
								       )
							       )}
			       </div>
		       );
}

export default DogList;
