import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import io from 'socket.io-client';
import { useAuth } from '../contexts/AuthContext';
import '../css/chat-app.css';
import '../styles/chatAppNotification.css';

interface UserWithBlocks {
  _id: string;
  blockedUsers?: string[];
}
interface Message {
  _id: string;
  sender: string;
  recipient: string;
  message: string;
  sentAt: string;
  messageType?: string;
  dogId?: string;
  isOwner?: boolean;
}
interface Conversation {
  _id: string;
  participants: string[];
}
const getApiUrl = () => {
  if (process.env.REACT_APP_API_URL) return process.env.REACT_APP_API_URL;
  if (window.location.protocol === 'https:') {
    return `https://${window.location.hostname}`;
  }
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return `http://${window.location.hostname}:3001`;
  }
  return `http://172.20.10.2:3001`;
};

const ChatApp: React.FC = () => {
    // Adoption message UI state
    const [adoptionDogId, setAdoptionDogId] = useState('');
    const [adoptionIsOwner, setAdoptionIsOwner] = useState(false);

  const { t } = useTranslation();
  const { user, token } = useAuth();
  const [onlineUsers, setOnlineUsers] = useState<{_id: string, userName: string, profilePicture?: string}[]>([]);
  const [selectedConvo, setSelectedConvo] = useState<Conversation | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [typing, setTyping] = useState(false);
  const [notification, setNotification] = useState<string | null>(null);
  const [userSearch, setUserSearch] = useState('');
  const [searchResults, setSearchResults] = useState<{_id: string, userName: string}[]>([]);
  const socketRef = useRef<ReturnType<typeof io> | null>(null);
  const userWithBlocks = user as UserWithBlocks | null;

  // Handler for sending a message
  const sendMessage = async () => {
    if (!input || !selectedConvo || !token) return;
    const recipient = selectedConvo.participants.find(id => id !== user._id);
    let messagePayload: any = {
      conversationId: selectedConvo._id,
      sender: user._id,
      recipient,
      message: input
    };
    // If adoptionDogId is set, send as adoption message
    if (adoptionDogId) {
      messagePayload.messageType = 'adoption';
      messagePayload.dogId = adoptionDogId;
      messagePayload.isOwner = adoptionIsOwner;
    }
    socketRef.current?.emit('sendMessage', messagePayload);
    await fetch(`${getApiUrl()}/api/chat/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(messagePayload)
    });
    // Optimistically add message to UI
    setMessages(prev => [
      ...prev,
      {
        _id: Math.random().toString(36).substr(2, 9),
        sender: user._id,
        recipient,
        message: input,
        sentAt: new Date().toISOString(),
        messageType: messagePayload.messageType,
        dogId: messagePayload.dogId,
        isOwner: messagePayload.isOwner
      }
    ]);
    setInput('');
    setAdoptionDogId('');
    setAdoptionIsOwner(false);
  };

  // Handler for input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    if (selectedConvo) {
      const recipient = selectedConvo.participants.find(id => id !== user._id);
      socketRef.current?.emit('typing', { recipient, sender: user._id });
    }
  };

  // Handler for deleting a conversation
  const handleDeleteConversation = async () => {
    if (!selectedConvo || !token) return;
    await fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    setSelectedConvo(null);
    setMessages([]);
  };

  // Block/unblock logic
    // Adoption handlers
    const handleConfirmAdoption = async (dogId: string, isOwner: boolean) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/confirm-adoption`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ dogId, isOwner })
        });
        if (res.ok) {
          setNotification(t('adoptionConfirmed'));
        }
      } catch (err) {
        setNotification(t('adoptionConfirmError'));
      }
    };

    const handleCancelAdoption = async (dogId: string) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/${dogId}/adopt-cancel`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ reason: '' })
        });
        if (res.ok) {
          setNotification(t('adoptionCanceled'));
        }
      } catch (err) {
        setNotification(t('adoptionCancelError'));
      }
    };
  const isBlocked = selectedConvo && userWithBlocks && userWithBlocks.blockedUsers && selectedConvo.participants.some(
    id => id !== userWithBlocks._id && userWithBlocks.blockedUsers!.includes(id)
  );
  const otherUserId = selectedConvo && userWithBlocks ? selectedConvo.participants.find(id => id !== userWithBlocks._id) : null;
  const handleBlockUnblock = async () => {
    if (!otherUserId || !token) return;
    const url = isBlocked ? '/api/chat/unblock' : '/api/chat/block';
    await fetch(`${getApiUrl()}${url}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
      body: JSON.stringify({ userId: user._id, blockId: otherUserId })
    });
  };

  useEffect(() => {
    if (user?._id && token) {
      socketRef.current = io(getApiUrl());
      socketRef.current.emit('join', user._id);
      socketRef.current.emit('getOnlineUsers');
      socketRef.current.on('onlineUsers', (users) => {
        setOnlineUsers(users.filter((u: any) => u._id !== user._id));
      });
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, [user, token]);

  useEffect(() => {
    if (selectedConvo && token) {
      fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(setMessages);
    }
  }, [selectedConvo, token]);

  const startConversation = async (otherUserId: string) => {
    if (!otherUserId || !token) return;
    const res = await fetch(`${getApiUrl()}/api/chat/conversation`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ userId: user._id, otherUserId })
    });
    const convo = await res.json();
    setSelectedConvo(convo);
  };

  return (
    <div className="chat-app-container">
      <div className="chat-app-sidebar">
        <input
          className="chat-app-search"
          type="text"
          value={userSearch}
          onChange={e => setUserSearch(e.target.value)}
          placeholder={t('searchUsersPlaceholder')}
        />
        {searchResults.length > 0 && (
          <ul style={{ listStyle: 'none', padding: 0, marginBottom: 4 }}>
            {searchResults.map(u => (
              <li key={u._id}>
                <button className="chat-app-convo-btn" onClick={() => startConversation(u._id)}>{u.userName}</button>
              </li>
            ))}
          </ul>
        )}
        <div className="chat-app-online-users">
          <b style={{ fontSize: 13, color: '#43b581' }}>Online Users</b>
          <ul style={{ listStyle: 'none', padding: 0, minHeight: 40 }}>
            {onlineUsers.filter(u => {
              const id = typeof u === 'string' ? u : u._id;
              return String(id) !== String(user._id);
            }).length === 0 && (
              <li style={{ color: '#aaa', fontSize: 12 }}>{t('noUsersOnline')}</li>
            )}
            {onlineUsers.filter(u => {
              const id = typeof u === 'string' ? u : u._id;
              return String(id) !== String(user._id);
            }).map(u => {
              let displayName = '';
              let profileImg = '../img/androcolored-80x80.jpg';
              if (typeof u === 'string') {
                displayName = u;
              } else if (u && typeof u === 'object') {
                displayName = u.userName || u._id || 'Unknown';
                if (u.profilePicture) {
                  profileImg = `${getApiUrl()}${u.profilePicture}`;
                }
              }
              return (
                <li key={typeof u === 'string' ? u : u._id} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 4, marginBottom: 8 }}>
                  <span className="chat-app-online-dot" style={{ position: 'absolute', left: 8, top: 8 }} />
                  <img
                    src={profileImg}
                    alt={displayName}
                    style={{ width: 40, height: 40, borderRadius: '50%', objectFit: 'cover', border: '2px solid #ddd', minWidth: '40px', minHeight: '40px', maxWidth: '60px', maxHeight: '60px', marginBottom: 4 }}
                    onError={e => { (e.target as HTMLImageElement).src = '../img/androcolored-80x80.jpg'; }}
                  />
                  <button className="chat-app-convo-btn" style={{ fontSize: 13, fontWeight: 500, marginTop: 2 }} onClick={() => startConversation(typeof u === 'string' ? u : u._id)}>{displayName}</button>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
      <div className="chat-app-main">
        {selectedConvo && (
          <div className="chat-app-actions-row">
            <button className="chat-app-action-btn delete" onClick={handleDeleteConversation}>{t('deleteConversation')}</button>
            <button className={`chat-app-action-btn ${isBlocked ? 'unblock' : 'block'}`} onClick={handleBlockUnblock}>{isBlocked ? t('unblock') : t('block')}</button>
            {/* Adoption buttons logic: show if message is adoption-related */}
            {messages.map(msg => (
              <div key={msg._id} className={`chat-app-message${user && msg.sender === user._id ? ' self' : ''}${msg.sender === null ? ' system' : ''}`}>
                <span className={`chat-app-bubble${user && msg.sender === user._id ? ' self' : ''}`}>{msg.message}</span>
                {msg.messageType === 'adoption' && msg.dogId && (
                  <div className="chat-app-adoption-actions">
                    <button className="chat-app-action-btn confirm" onClick={() => handleConfirmAdoption(msg.dogId, msg.isOwner)}>{t('confirmAdoption')}</button>
                    <button className="chat-app-action-btn cancel" onClick={() => handleCancelAdoption(msg.dogId)}>{t('cancelAdoption')}</button>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
        {selectedConvo && (
          <div className="chat-app-messages">
            {messages.map(msg => (
              <div
                key={msg._id}
                className={`chat-app-message${user && msg.sender === user._id ? ' self' : ''}${msg.sender === null ? ' system' : ''}`}
                style={msg.sender === null ? { textAlign: 'center', color: '#888', fontStyle: 'italic', background: '#f9f9f9', border: 'none', margin: '8px 0' } : {}}
              >
                <span className={`chat-app-bubble${user && msg.sender === user._id ? ' self' : ''}`}>{msg.message}</span>
              </div>
            ))}
            {typing && <div className="chat-app-typing">{t('typing')}</div>}
          </div>
        )}
        {selectedConvo && (
          <div className="chat-app-input-row">
            <input
              className="chat-app-input"
              type="text"
              value={input}
              onChange={handleInputChange}
              onKeyDown={e => { if (e.key === 'Enter') sendMessage(); }}
              placeholder={t('typeMessagePlaceholder') || 'Type a message...'}
            />
            <button
              className="chat-app-send-btn"
              onClick={sendMessage}
              disabled={!input.trim()}
            >{t('send') || 'Send'}</button>
            {/* Adoption message UI */}
            <div style={{ marginTop: 16, padding: 12, background: '#f6f6f6', borderRadius: 8, border: '1px solid #ddd', maxWidth: 340, width: '100%' }}>
              <div style={{ fontWeight: 600, marginBottom: 8 }}>Send Adoption Message</div>
              <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
                <input
                  type="text"
                  placeholder="Dog ID for adoption"
                  value={adoptionDogId}
                  onChange={e => setAdoptionDogId(e.target.value)}
                  style={{ width: '100%', minWidth: 0 }}
                />
                <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <input
                    type="checkbox"
                    checked={adoptionIsOwner}
                    onChange={e => setAdoptionIsOwner(e.target.checked)}
                  />
                  {t('isOwner') || 'I am the owner'}
                </label>
                <button
                  className="chat-app-send-btn"
                  onClick={sendMessage}
                  disabled={!adoptionDogId}
                  style={{ background: '#43b581', color: '#fff', width: '100%', minWidth: 0, padding: '10px 0', fontWeight: 600 }}
                >{t('sendAdoptionMessage') || 'Send Adoption Message'}</button>
              </div>
            </div>
          </div>
        )}
        {notification && (
          <div className="chat-app-notification">{notification}</div>
        )}
        <button
          onClick={() => window.dispatchEvent(new CustomEvent('closeChatModal'))}
          style={{
            position: 'absolute',
            top: 10,
            right: 10,
            width: 56,
            height: 56,
            backgroundColor: '#e74c3c',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
            cursor: 'pointer',
            zIndex: 2147483647,
            padding: 0
          }}
          title="Close"
          aria-label="Close"
        >
          <span style={{
            fontSize: 40,
            fontWeight: 900,
            lineHeight: '56px',
            width: '100%',
            textAlign: 'center',
            display: 'block',
            userSelect: 'none',
            letterSpacing: 2
          }}>Ã—</span>
        </button>
      </div>
    </div>
  );
}

export default ChatApp;
