import React, { useEffect, useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';

interface EditDogFormData {
  name: string;
  breed?: string;
}

interface EditDogModalProps {
  dog: any;
  isSingleDog: boolean;
  onSave: (dog: any) => void;
}

const EditDogModal: React.FC<EditDogModalProps> = ({ dog, isSingleDog, onSave }) => {
  const { t } = useTranslation();
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });
  const { register, handleSubmit } = useForm<EditDogFormData>({ defaultValues: dog });
  useEffect(() => {
    const handleResize = () => setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    console.log('[EditDogModal] Mounted with dog:', dog);
  }, [dog]);

  const onSubmit: SubmitHandler<EditDogFormData> = fields => {
    console.log('[EditDogModal] onSubmit called with fields:', fields);
    onSave({ ...dog, ...fields });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      background: 'rgba(0,0,255,0.2)',
      zIndex: 999999,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      pointerEvents: 'auto',
    }}>
      <form
        onSubmit={handleSubmit(onSubmit)}
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '1.2rem',
          fontSize: '1rem',
          background: 'white',
          border: '2px solid #3498db',
          borderRadius: 12,
          padding: '2rem',
          minWidth: '320px',
          minHeight: '220px',
          maxWidth: '90vw',
          maxHeight: '90vh',
          zIndex: 9999999,
          boxSizing: 'border-box',
          overflow: 'auto',
        }}
      >
        <label>{t('adddog.name')}</label>
        <input
          {...register('name')}
          defaultValue={dog.name || ''}
          required
          style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
        />
        <label>{t('adddog.breed')}</label>
        <input
          {...register('breed')}
          defaultValue={dog.breed || ''}
          style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
        />
        <div style={{
          display: 'flex',
          justifyContent: 'flex-end',
          marginTop: '1.5rem',
          position: 'sticky',
          bottom: 0,
          background: 'white',
          zIndex: 10,
          paddingBottom: 1
        }}>
          <button
            type="submit"
            style={{
              background: '#3498db',
              color: '#fff',
              border: 'none',
              borderRadius: 6,
              padding: '0.5rem 1.2rem',
              fontWeight: 'bold',
              fontSize: '1rem',
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(52,152,219,0.08)'
            }}
          >
            {t('editdog.save').replace('editdog.', '').replace('adddog.', '') || 'Save'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditDogModal;
