import React from 'react';
import CardSmall from '../CardSmall';
import Search from '../Search';
import { useTranslation } from 'react-i18next';


// --- Types ---
type DogType = {
	_id: string;
	name: string;
	breed: string;
	age: number;
	gender: string;
	place?: string;
	location?: string;
	images: string[];
	adoptionStatus?: string;
	adoptionQueue?: string[];
	vaccinated?: boolean;
	video?: { url?: string };
};

type MapModalType = {
	place: string;
	coords: { lat: number; lon: number };
};

// --- ErrorBoundary ---
class ErrorBoundary extends React.Component<{children: React.ReactNode}, {hasError: boolean, error: any}> {
	constructor(props: any) {
		super(props);
		this.state = { hasError: false, error: null };
	}
	static getDerivedStateFromError(error: any) {
		return { hasError: true, error };
	}
	componentDidCatch(error: any, info: any) {}
	render() {
		if (this.state.hasError) {
			return <div style={{color:'red',padding:24}}>An error occurred: {String(this.state.error)}</div>;
		}
		return this.props.children;
	}
}

// --- DogList ---
function DogList() {
	const [dogs, setDogs] = React.useState<DogType[]>([]);
	const [viewDog, setViewDog] = React.useState<DogType | null>(null);
	const [mapModal, setMapModal] = React.useState<MapModalType | null>(null);
	const [searchTerm, setSearchTerm] = React.useState('');
	const [genderFilter, setGenderFilter] = React.useState('');
	const [sizeFilter, setSizeFilter] = React.useState('');
	const [locationFilter, setLocationFilter] = React.useState('');
	const [availableLocations, setAvailableLocations] = React.useState<string[]>([]);
	const { t } = useTranslation();

	function toAbsUrl(url?: string) {
		if (!url) return '';
		if (/^https?:\/\//.test(url)) return url;
		const apiBase = process.env.REACT_APP_API_URL || 'http://localhost:3001';
		if (url.startsWith('/uploads/') || url.startsWith('/u/dogs/')) {
			return apiBase + url;
		}
		return url;
	}

	function handleRemove(dogId: string) {
		if (window.confirm('Are you sure you want to remove this dog?')) {
			setDogs(dogs => dogs.filter(d => d._id !== dogId));
			// Optionally, send delete request to backend here
		}
	}

	React.useEffect(() => {
		const apiBase = process.env.REACT_APP_API_URL || 'http://localhost:3001';
		fetch(`${apiBase}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				const mapped = Array.isArray(data)
					? data.map((dog: any) => ({
						...dog,
						place: dog.place || dog.location,
						gender: dog.gender || (dog.gender === undefined ? '' : dog.gender),
						images: Array.isArray(dog.images) ? dog.images : [],
					}))
					: [];
				setDogs(mapped);
			});
	}, []);

	const handleShowMap = async (place: string) => {
		const res = await fetch(
			`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(place)}`
		);
		const data = await res.json();
		if (data && data.length > 0) {
			setMapModal({
				place,
				coords: { lat: parseFloat(data[0].lat), lon: parseFloat(data[0].lon) },
			});
		} else {
			alert('Location not found');
		}
	};

	return (
		<ErrorBoundary>
			<>
				<h1 style={{ textAlign: 'center', margin: '32px 0 16px', fontWeight: 700, fontSize: '2rem', color: '#3a3a3a' }}>
					Pronađi svog zauvijek prijatelja
				</h1>
				<div style={{ maxWidth: 600, margin: '0 auto 24px' }}>
					<Search
						searchTerm={searchTerm}
						onSearchChange={setSearchTerm}
						genderFilter={genderFilter}
						onGenderChange={setGenderFilter}
						sizeFilter={sizeFilter}
						onSizeChange={setSizeFilter}
						locationFilter={locationFilter}
						onLocationChange={setLocationFilter}
						availableLocations={availableLocations}
					/>
				</div>
				{/* Dog list grid */}
				<div style={{ display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center', margin: '32px 0' }}>
					{dogs.map(dog => {
						const { video, images, ...rest } = dog;
						const imagesProp = Array.isArray(images)
							? images.filter(Boolean).map((url) => typeof url === 'string' ? { url } : url)
							: undefined;
						const cardProps = {
							...rest,
							...(imagesProp ? { images: imagesProp } : {}),
							...(video && video.url ? { video: { url: video.url } } : {}),
							canEdit: true,
							onEdit: () => setViewDog(dog),
							onRemove: () => handleRemove(dog._id),
						};
						return (
							<CardSmall
								key={dog._id}
								{...cardProps}
								onViewDetails={() => setViewDog(dog)}
							/>
						);
					})}
				</div>

				{/* Dog details modal (example) */}
				{viewDog && (
					<div
						style={{
							position: 'fixed',
							top: 0,
							left: 0,
							width: '100vw',
							height: '100vh',
							background: 'rgba(0,0,0,0.7)',
							zIndex: 2147483647,
							display: 'flex',
							alignItems: 'center',
							justifyContent: 'center',
							padding: 0,
						}}
					>
						<div
							style={{
								background: '#fff',
								borderRadius: 16,
								maxWidth: 420,
								width: '95vw',
								maxHeight: '90vh',
								overflowY: 'auto',
								position: 'relative',
								padding: 24,
								boxSizing: 'border-box',
							}}
						>
							<button
								type="button"
								onClick={() => setViewDog(null)}
								style={{
									position: 'absolute',
									top: 12,
									right: 12,
									width: 40,
									height: 40,
									background: '#e74c3c',
									color: '#fff',
									border: 'none',
									borderRadius: '50%',
									fontSize: '1.5rem',
									fontWeight: 'bold',
									cursor: 'pointer',
									zIndex: 2,
								}}
								aria-label="Close"
								title="Close"
							>
								×
							</button>
							{/* Show main image or placeholder */}
							{Array.isArray(viewDog.images) && viewDog.images.length > 0 ? (
								<img
									src={toAbsUrl(viewDog.images[0])}
									alt={viewDog.name}
									style={{ width: '100%', height: 220, objectFit: 'cover', borderRadius: 12, marginBottom: 16, background: '#eee' }}
									onError={e => (e.currentTarget.style.display = 'none')}
								/>
							) : (
								<div style={{ width: '100%', height: 220, background: '#eee', borderRadius: 12, marginBottom: 16 }} />
							)}
							<h2 style={{ marginBottom: 16 }}>{viewDog.name}</h2>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Breed:</strong> {viewDog.breed}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Age:</strong> {viewDog.age}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Gender:</strong> {viewDog.gender}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}>
								<strong>Place:</strong>
								{viewDog.place && (
									<span
										style={{ color: '#1976d2', textDecoration: 'underline', cursor: 'pointer', marginLeft: 4 }}
										onClick={() => handleShowMap(viewDog.place)}
									>
										{viewDog.place}
									</span>
								)}
							</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Status:</strong> {viewDog.adoptionStatus}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Adoption Queue:</strong> {viewDog.adoptionQueue && viewDog.adoptionQueue.length ? viewDog.adoptionQueue.join(', ') : 'None'}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Vaccinated:</strong> {viewDog.vaccinated ? 'Yes' : 'No'}</div>
							<div style={{ marginBottom: 8, fontSize: '1.15rem' }}><strong>Video:</strong> {viewDog.video && viewDog.video.url ? viewDog.video.url : 'None'}</div>
							<div style={{ display: 'flex', gap: 12, marginTop: 16 }}>
								<button type="button" style={{ background: '#dbb69d', color: '#fff', border: 'none', fontWeight: 600, padding: '8px 18px', borderRadius: 6, cursor: 'pointer' }} onClick={() => alert('Added to wishlist!')}>Add to Wishlist</button>
								<button type="button" style={{ background: '#43a047', color: '#fff', border: 'none', fontWeight: 600, padding: '8px 18px', borderRadius: 6, cursor: 'pointer' }} onClick={() => alert('Adopt functionality coming soon!')}>Adopt</button>
							</div>
						</div>
					</div>
				)}

				{/* Map modal (global, always top-level) */}
				{mapModal && !viewDog && (
					<div
						style={{
							position: 'fixed',
							top: '50%',
							left: '50%',
							transform: 'translate(-50%, -50%)',
							background: '#fff',
							border: '2px solid #3498db',
							borderRadius: 16,
							zIndex: 2147483647,
							minWidth: 320,
							maxWidth: 640,
							padding: 16,
							width: '90vw',
							maxHeight: '90vh',
							boxSizing: 'border-box',
							overflow: 'auto',
						}}
					>
						<iframe
							title="Map"
							width="100%"
							height="250"
							style={{ borderRadius: 12, border: '1px solid #ccc', marginTop: 16, zIndex: 1 }}
							src={`https://www.openstreetmap.org/export/embed.html?bbox=${mapModal.coords.lon-0.01},${mapModal.coords.lat-0.01},${mapModal.coords.lon+0.01},${mapModal.coords.lat+0.01}&layer=mapnik&marker=${mapModal.coords.lat},${mapModal.coords.lon}`}
							allowFullScreen
						/>
						<div
							style={{
								position: 'absolute',
								top: 0,
								right: 0,
								zIndex: 2147483647,
								background: 'rgba(255,255,255,0.98)',
								borderBottomLeftRadius: 16,
								padding: 4,
								boxShadow: '0 2px 12px rgba(0,0,0,0.22)',
								pointerEvents: 'auto',
							}}
						>
							<button
								type="button"
								onClick={() => setMapModal(null)}
								style={{
									width: 44,
									height: 44,
									background: '#fff',
									border: '3px solid #e74c3c',
									color: '#e74c3c',
									borderRadius: '50%',
									fontSize: '2rem',
									fontWeight: 'bold',
									cursor: 'pointer',
									zIndex: 2147483647,
									display: 'flex',
									alignItems: 'center',
									justifyContent: 'center',
									touchAction: 'manipulation',
									pointerEvents: 'auto',
								}}
								aria-label="Close"
								title="Close"
							>
								×
							</button>
						</div>
						<div style={{ fontSize: '0.9rem', marginTop: 4 }}>
							<a
								href={`https://www.openstreetmap.org/?mlat=${mapModal.coords.lat}&mlon=${mapModal.coords.lon}#map=16/${mapModal.coords.lat}/${mapModal.coords.lon}`}
								target="_blank"
								rel="noopener noreferrer"
							>
								View larger map
							</a>
						</div>
					</div>
				)}
			</>
			</ErrorBoundary>
				);
			}

			export default DogList;
