  // TEMP: Function to send an adoption message for a given dogId
  const sendAdoptionMessage = async (dogId: string) => {
    if (!selectedConvo || !token) return;
    const recipient = selectedConvo.participants.find(id => id !== user._id);
    const messagePayload: any = {
      conversationId: selectedConvo._id,
      sender: user._id,
      recipient,
      message: t('adoptionRequest') || 'Adoption request',
      messageType: 'adoption',
      dogId,
      isOwner: adoptionIsOwner
    };
    socketRef.current?.emit('sendMessage', messagePayload);
    await fetch(`${getApiUrl()}/api/chat/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(messagePayload)
    });
    setMessages(prev => [
      ...prev,
      {
        _id: Math.random().toString(36).substr(2, 9),
        sender: user._id,
        recipient,
        message: messagePayload.message,
        sentAt: new Date().toISOString(),
        messageType: 'adoption',
        dogId,
        isOwner: adoptionIsOwner
      }
    ]);
    setAdoptionDogId(dogId);
  };
import React, { useState, useEffect, useRef } from 'react';
import { useTranslation } from 'react-i18next';
import io from 'socket.io-client';
import { useAuth } from '../contexts/AuthContext';
import '../css/chat-app.css';
import '../styles/chatAppNotification.css';

interface UserWithBlocks {
  _id: string;
  blockedUsers?: string[];
}
interface Message {
  _id: string;
  sender: string;
  recipient: string;
  message: string;
  sentAt: string;
  messageType?: string;
  dogId?: string;
  isOwner?: boolean;
}
interface Conversation {
  _id: string;
  participants: string[];
}
const getApiUrl = () => {
  if (process.env.REACT_APP_API_URL) return process.env.REACT_APP_API_URL;
  if (window.location.protocol === 'https:') {
    return `https://${window.location.hostname}`;
  }
  if (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1') {
    return `http://${window.location.hostname}:3001`;
  }
  return `http://172.20.10.2:3001`;
};

const ChatApp: React.FC = () => {
  const { t } = useTranslation();
  const { user, token } = useAuth();
  const [dogStatusMap, setDogStatusMap] = useState<Record<string, string>>({});
  const [dogOwnerMap, setDogOwnerMap] = useState<Record<string, string>>({});
  const [adoptionDogId, setAdoptionDogId] = useState('');
  const [adoptionIsOwner, setAdoptionIsOwner] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [onlineUsers, setOnlineUsers] = useState<{_id: string, userName: string, profilePicture?: string}[]>([]);
  const [selectedConvo, setSelectedConvo] = useState<Conversation | null>(null);
  const [input, setInput] = useState('');
  const [typing] = useState(false);
  const [notification, setNotification] = useState<string | null>(null);
  const [userSearch, setUserSearch] = useState('');
  const socketRef = useRef<ReturnType<typeof io> | null>(null);
  const userWithBlocks = user as UserWithBlocks | null;

  // DEBUG: Log mount and key state after variables are defined
  // eslint-disable-next-line no-console
  console.info('DEBUG: ChatApp mount', { selectedConvo, token });
  // Removed duplicate variable declarations

  // Fetch and cache dog owners for all dogIds in messages and adoptionDogId
  useEffect(() => {
    const ids = new Set<string>();
    if (adoptionDogId) ids.add(adoptionDogId);
    messages.forEach(m => { if (m.dogId) ids.add(m.dogId); });
    const fetchOwners = async () => {
      const newMap: Record<string, string> = {};
      await Promise.all(Array.from(ids).map(async id => {
        try {
          const res = await fetch(`${getApiUrl()}/api/dogs/${id}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          if (res.ok) {
            const dog = await res.json();
            newMap[id] = dog.owner;
          }
        } catch {}
      }));
      setDogOwnerMap(newMap);
    };
    if (token && ids.size > 0) fetchOwners();
  }, [adoptionDogId, messages, token]);
  // Removed duplicate variable declarations

  // Handler for sending a message
  const sendMessage = async () => {
    if (!input || !selectedConvo || !token) return;
    const recipient = selectedConvo.participants.find(id => id !== user._id);
    let messagePayload: any = {
      conversationId: selectedConvo._id,
      sender: user._id,
      recipient,
      message: input
    };
    // If adoptionDogId is set, send as adoption message
    if (adoptionDogId) {
      messagePayload.messageType = 'adoption';
      messagePayload.dogId = adoptionDogId;
      messagePayload.isOwner = adoptionIsOwner;
    }
    socketRef.current?.emit('sendMessage', messagePayload);
    await fetch(`${getApiUrl()}/api/chat/message`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(messagePayload)
    });
    // Optimistically add message to UI
    setMessages(prev => [
      ...prev,
      {
        _id: Math.random().toString(36).substr(2, 9),
        sender: user._id,
        recipient,
        message: input,
        sentAt: new Date().toISOString(),
        messageType: messagePayload.messageType,
        dogId: messagePayload.dogId,
        isOwner: messagePayload.isOwner
      }
    ]);
    setInput('');
    // Do not clear adoptionDogId or adoptionIsOwner here; keep them until adoption is confirmed or canceled
  };

  // Handler for input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    if (selectedConvo) {
      const recipient = selectedConvo.participants.find(id => id !== user._id);
      socketRef.current?.emit('typing', { recipient, sender: user._id });
    }
  };

  // Handler for deleting a conversation
  const handleDeleteConversation = async () => {
    if (!selectedConvo || !token) return;
    await fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${token}` }
    });
    setSelectedConvo(null);
    setMessages([]);
  };

  // Block/unblock logic
    // Adoption handlers
    const handleConfirmAdoption = async (dogId: string, isOwner: boolean) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/confirm-adoption`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ dogId, isOwner })
        });
        if (res.ok) {
          setNotification(t('adoptionConfirmed'));
          setAdoptionDogId('');
          setAdoptionIsOwner(false);
        }
      } catch (err) {
        setNotification(t('adoptionConfirmError'));
      }
    };

    const handleCancelAdoption = async (dogId: string) => {
      if (!token) return;
      try {
        const res = await fetch(`${getApiUrl()}/api/dogs/${dogId}/adopt-cancel`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
          body: JSON.stringify({ reason: '' })
        });
        if (res.ok) {
          setNotification(t('adoptionCanceled'));
          setAdoptionDogId('');
          setAdoptionIsOwner(false);
        }
      } catch (err) {
        setNotification(t('adoptionCancelError'));
      }
    };
  const isBlocked = selectedConvo && userWithBlocks && userWithBlocks.blockedUsers && selectedConvo.participants.some(
    id => id !== userWithBlocks._id && userWithBlocks.blockedUsers!.includes(id)
  );
  const otherUserId = selectedConvo && userWithBlocks ? selectedConvo.participants.find(id => id !== userWithBlocks._id) : null;
  const handleBlockUnblock = async () => {
    if (!otherUserId || !token) return;
    const url = isBlocked ? '/api/chat/unblock' : '/api/chat/block';
    await fetch(`${getApiUrl()}${url}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${token}` },
      body: JSON.stringify({ userId: user._id, blockId: otherUserId })
    });
  };

  useEffect(() => {
    if (user?._id && token) {
      socketRef.current = io(getApiUrl());
      socketRef.current.emit('join', user._id);
      socketRef.current.emit('getOnlineUsers');
      socketRef.current.on('onlineUsers', (users) => {
        setOnlineUsers(users.filter((u: any) => u._id !== user._id));
      });
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, [user, token]);

  useEffect(() => {
    if (selectedConvo && token) {
      fetch(`${getApiUrl()}/api/chat/messages/${selectedConvo._id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          console.log('DEBUG: fetched messages', data);
          setMessages(data);
          // Auto-set adoptionDogId if there is an adoption message (regardless of status map)
          const adoptionMsg = data.find((m: any) => m.messageType === 'adoption' && m.dogId);
          if (adoptionMsg && adoptionMsg.dogId) {
            setAdoptionDogId(adoptionMsg.dogId);
            setAdoptionIsOwner(adoptionMsg.isOwner || false);
          }
        });
    }
  }, [selectedConvo, token]);

  // Fetch adoption status for all dogIds in messages and adoptionDogId
  useEffect(() => {
    const ids = new Set<string>();
    if (adoptionDogId) ids.add(adoptionDogId);
    messages.forEach(m => { if (m.dogId) ids.add(m.dogId); });
    const fetchStatuses = async () => {
      const newMap: Record<string, string> = {};
      await Promise.all(Array.from(ids).map(async id => {
        try {
          const res = await fetch(`${getApiUrl()}/api/dogs/${id}`, {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          if (res.ok) {
            const dog = await res.json();
            newMap[id] = dog.adoptionStatus;
          }
        } catch {}
      }));
      setDogStatusMap(newMap);
      // if (adoptionDogId) setAdoptionDogStatus(newMap[adoptionDogId] || null);
    };
    if (token && ids.size > 0) fetchStatuses();
  }, [adoptionDogId, messages, token]);

  const startConversation = async (otherUserId: string) => {
    if (!otherUserId || !token) return;
    const res = await fetch(`${getApiUrl()}/api/chat/conversation`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ userId: user._id, otherUserId })
    });
    const convo = await res.json();
    setSelectedConvo(convo);
  };

  return (
    <div className="chat-app-container" style={{position: 'fixed', right: '60px', bottom: '32px', display: 'flex', flexDirection: 'column', width: '480px', maxWidth: '100vw', height: '80vh', zIndex: 1002}}>
      <button
        className="chat-app-close-btn"
        onClick={() => window.dispatchEvent(new CustomEvent('closeChatModal'))}
        title="Close"
        aria-label="Close"
        type="button"
      >
        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" style={{display:'block',margin:'auto'}}>
          <circle cx="8" cy="8" r="7" fill="#e74c3c"/>
          <path d="M4.5 4.5L11.5 11.5M11.5 4.5L4.5 11.5" stroke="#fff" strokeWidth="2" strokeLinecap="round"/>
        </svg>
      </button>
      <div className="chat-app-sidebar" style={{width: '100%', minWidth: 0, maxWidth: '100%', borderBottom: '1px solid #e3e3e3'}}>
        <div className="chat-app-online-users">
          <b style={{ fontSize: 13, color: '#43b581' }}>Online Users</b>
          <ul style={{ listStyle: 'none', padding: 0, minHeight: 40 }}>
            {onlineUsers.filter(u => {
              const id = typeof u === 'string' ? u : u._id;
              return String(id) !== String(user._id);
            }).length === 0 && (
              <li style={{ color: '#aaa', fontSize: 12 }}>{t('noUsersOnline')}</li>
            )}
            {onlineUsers.filter(u => {
              const id = typeof u === 'string' ? u : u._id;
              return String(id) !== String(user._id);
            }).map(u => {
              let displayName = '';
              let profileImg = '../img/androcolored-80x80.jpg';
              if (typeof u === 'string') {
                displayName = u;
              } else if (u && typeof u === 'object') {
                displayName = u.userName || u._id || 'Unknown';
                if (u.profilePicture) {
                  profileImg = `${getApiUrl()}${u.profilePicture}`;
                }
              }
              return (
                <li key={typeof u === 'string' ? u : u._id} className="chat-app-user-row">
                  <span className="chat-app-online-dot" style={{ position: 'absolute', left: 8, top: 18 }} />
                  <img
                    src={profileImg}
                    alt={displayName}
                    style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #ddd', minWidth: '36px', minHeight: '36px', maxWidth: '40px', maxHeight: '40px', marginRight: 8 }}
                    onError={e => { (e.target as HTMLImageElement).src = '../img/androcolored-80x80.jpg'; }}
                  />
                  <button className="chat-app-convo-btn" style={{ fontSize: 13, fontWeight: 500, margin: 0, padding: 0, background: 'none', border: 'none', color: '#1f4b72', cursor: 'pointer' }} onClick={() => startConversation(typeof u === 'string' ? u : u._id)}>{displayName}</button>
                </li>
              );
            })}
          </ul>
        </div>
        <input
          className="chat-app-search"
          type="text"
          value={userSearch}
          onChange={e => setUserSearch(e.target.value)}
          placeholder={t('searchUsersPlaceholder')}
        />
      </div>
      <div className="chat-app-main" style={{flex: 1, minWidth: 0, minHeight: 0, display: 'flex', flexDirection: 'column', background: 'none', boxShadow: 'none'}}>
        {selectedConvo && (
          <>
            <div className="chat-app-actions-row">
              <button className="chat-app-action-btn delete" onClick={handleDeleteConversation}>{t('deleteConversation')}</button>
              <button className={`chat-app-action-btn ${isBlocked ? 'unblock' : 'block'}`} onClick={handleBlockUnblock}>{isBlocked ? t('unblock') : t('block')}</button>
            </div>
            <div className="chat-app-messages" style={{
              flex: 1,
              minHeight: 0,
              maxHeight: '100%',
              overflowY: 'auto',
              display: 'flex',
              flexDirection: 'column',
              background: '#f5f8fa',
              border: '1.5px solid #e3e3e3',
              borderRadius: 10,
              marginBottom: 10,
              boxShadow: '0 2px 8px rgba(33,150,243,0.06)'
            }}>
              {messages.map(msg => (
                <div
                  key={msg._id}
                  className={`chat-app-message${user && msg.sender === user._id ? ' self' : ''}${msg.sender === null ? ' system' : ''}`}
                  style={msg.sender === null ? { textAlign: 'center', color: '#888', fontStyle: 'italic', background: '#f9f9f9', border: 'none', margin: '8px 0' } : {}}
                >
                  <span className={`chat-app-bubble${user && msg.sender === user._id ? ' self' : ''}`}>{msg.message}</span>
                </div>
              ))}
              {typing && <div className="chat-app-typing">{t('typing')}</div>}
            </div>
            {/* DEBUG: Show adoptionDogId, dogOwnerMap, user._id for troubleshooting */}
            <div style={{background:'#e3f2fd', color:'#1565c0', fontSize:12, padding:8, margin:'8px 0', borderRadius:6}}>
              <div><b>adoptionDogId:</b> {adoptionDogId || '(empty)'}</div>
              <div><b>dogOwnerMap[adoptionDogId]:</b> {adoptionDogId ? dogOwnerMap[adoptionDogId] : '(n/a)'}</div>
              <div><b>user._id:</b> {user && user._id}</div>
              {/* TEMP: Button to send adoption message for testing */}
              <div style={{marginTop:8}}>
                <input id="adoption-dogid-input" placeholder="Dog ID for adoption" style={{fontSize:12, padding:2, marginRight:4}} />
                <button style={{fontSize:12, padding:'2px 8px'}} onClick={() => {
                  const dogId = (document.getElementById('adoption-dogid-input') as HTMLInputElement)?.value;
                  if (dogId) sendAdoptionMessage(dogId);
                }}>Send Adoption Message</button>
              </div>
            </div>
            {/* Adoption action buttons: always show if adoptionDogId is set and user is not the owner */}
            {adoptionDogId && selectedConvo && (() => {
              const dogOwner = dogOwnerMap[adoptionDogId];
              if (!dogOwner || dogOwner !== user._id) {
                return (
                  <div className="chat-app-message">
                    <span className="chat-app-bubble">{t('adoptionRequest')}</span>
                    <div className="chat-app-adoption-actions">
                      <button className="chat-app-action-btn confirm" onClick={() => handleConfirmAdoption(adoptionDogId, false)}>{t('confirmAdoption')}</button>
                      <button className="chat-app-action-btn cancel" onClick={() => handleCancelAdoption(adoptionDogId)}>{t('cancelAdoption')}</button>
                    </div>
                  </div>
                );
              }
              return null;
            })()}
          </>
        )}
        {selectedConvo && (
          <div className="chat-app-input-row">
            <input
              className="chat-app-input"
              type="text"
              value={input}
              onChange={handleInputChange}
              onKeyDown={e => { if (e.key === 'Enter') sendMessage(); }}
              placeholder={t('typeMessagePlaceholder') || 'Type a message...'}
            />
            <button
              className="chat-app-send-btn"
              onClick={sendMessage}
              disabled={!input.trim()}
            >{t('send') || 'Send'}</button>
          </div>
        )}
        {notification && (
          <div className="chat-app-notification">{notification}</div>
        )}
      </div>
    </div>
  );
}

export default ChatApp;
