import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTranslation } from 'react-i18next';

interface ImageType {
	url: string;
	width?: number;
}
interface VideoType {
	url: string;
	poster?: ImageType[];
}
interface ThumbnailType {
	url: string;
}
export interface CardSmallProps {
	_id?: string;
	name: string;
	breed?: string;
	age?: number;
	gender?: string;
	place?: string;
	video?: VideoType;
	thumbnail?: ThumbnailType;
	canEdit?: boolean;
	onViewDetails?: (event?: React.MouseEvent<any, MouseEvent>) => void;
	onDogUpdate?: (props: any) => void;
	onEdit?: (event?: React.MouseEvent<HTMLButtonElement, MouseEvent>) => void;
	onRemove?: () => void;
	smallImage?: boolean;
	compactDesktop?: boolean;
	vaccinated?: boolean;
	likes?: number;
	images?: ImageType[];
	adoptionStatus?: string;
	adoptionQueue?: any[];
}

const CardSmall: React.FC<CardSmallProps> = (props) => {
	const { t } = useTranslation();
	const { isAuthenticated, isInWishlist, addToWishlist, removeFromWishlist } = useAuth();
	const [wishlistLoading, setWishlistLoading] = React.useState(false);
	const {
			       name = 'Unknown',
			       breed = 'Unknown',
			       age,
			       gender,
			       place,
			       video,
			       thumbnail,
			       canEdit,
			       onViewDetails,
			       onEdit,
			       onRemove,
			       vaccinated,
			       images,
			       adoptionStatus,
		       } = props;

	function toAbsUrl(url?: string) {
		if (!url) return url;
		const cacheBuster = (window as any).__EKY_IMAGE_CB || ((window as any).__EKY_IMAGE_CB = Date.now());
		// Use backend URL from env or default
		const apiBase = process.env.REACT_APP_API_URL || 'http://localhost:3001';
		if (/^https?:\/\//.test(url)) return url + (url.includes('?') ? '&' : '?') + 'cb=' + cacheBuster;
		if (url.startsWith('/uploads/') || url.startsWith('/u/dogs/')) {
			// Prepend backend URL if not already absolute
			return apiBase + url + '?cb=' + cacheBuster;
		}
		return url;
	}

	let largestImgUrl: string | undefined = undefined;
				// Improved: Only count unique base images (ignore size/hash/extension variants)
				const getImageBase = (url: string) => {
					// Remove query params
					let cleanUrl = url.split('?')[0];
					// Remove directory, get filename
					const filename = cleanUrl.substring(cleanUrl.lastIndexOf('/') + 1);
					// Remove size/hash/extension: keep up to first dash before size/hash/orig, or just before extension
					// Examples:
