import React, { useEffect, useState } from 'react';
import CardSmall from '../CardSmall';
import { useTranslation } from 'react-i18next';
import EditDogModal from './EditDogModal';
import RemoveDogModal from './RemoveDogModal';
import DogDetails from './DogDetails';

interface DogType {
  _id: string;
  name: string;
  breed?: string;
  age?: number;
  gender?: string;
  place?: string;
  location?: string;
  video?: any;
  thumbnail?: any;
  canEdit?: boolean;
  vaccinated?: boolean;
  images?: { url: string; width?: number }[];
  adoptionStatus?: string;
}


const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';


function DogList() {
	const { t } = useTranslation();
	const [dogs, setDogs] = useState<DogType[]>([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState<string | null>(null);
	const [editDog, setEditDog] = useState<DogType | null>(null);
	const [removeDog, setRemoveDog] = useState<DogType | null>(null);
	const [detailsDog, setDetailsDog] = useState<DogType | null>(null);

	useEffect(() => {
		setLoading(true);
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			})
			.catch(err => {
				setError('Failed to load dogs');
				setLoading(false);
			});
	}, []);

		const handleEdit = (dog: DogType) => setEditDog(dog);
		const handleRemove = (dog: DogType) => setRemoveDog(dog);
		const handleDetails = (dog: DogType) => setDetailsDog(dog);

	const handleEditSave = async (updatedDog: DogType) => {
					isDesktop ? (
						<div style={{ width: '100%', display: 'flex', flexDirection: 'column', gap: 12 }}>
							{dogs.map(dog => (
								<div
									key={dog._id}
									style={{
										border: detailsDog?._id === dog._id ? '2px solid #007bff' : '2px solid transparent',
										borderRadius: 12,
										background: detailsDog?._id === dog._id ? '#eaf3ff' : 'transparent',
										cursor: 'pointer',
										transition: 'border 0.2s, background 0.2s',
									}}
									onClick={() => setDetailsDog(dog)}
								>
									<CardSmall
										{...dog}
										canEdit={true}
										onEdit={() => handleEdit(dog)}
										onRemove={() => handleRemove(dog)}
										onViewDetails={() => handleDetails(dog)}
									/>
								</div>
							))}
						</div>
					) : (
						dogs.map(dog => (
							<CardSmall
								key={dog._id}
								{...dog}
								canEdit={true}
								onEdit={() => handleEdit(dog)}
								onRemove={() => handleRemove(dog)}
								onViewDetails={() => handleDetails(dog)}
							/>
						))
					)
		fetch(`${API_URL}/api/dogs`)
			.then(res => res.json())
			.then(data => {
				setDogs(Array.isArray(data) ? data : []);
				setLoading(false);
			});
	};

	if (loading) return <div>{t('doglist.loading') || 'Loading dogs...'}</div>;
	if (error) return <div style={{ color: 'red' }}>{error}</div>;

		       // Responsive: side-by-side on desktop, modal on mobile
		       const isDesktop = typeof window !== 'undefined' && window.innerWidth >= 900;

		       return (
			       <div style={{
				       display: isDesktop ? 'flex' : 'block',
				       flexDirection: isDesktop ? 'row' : undefined,
				       alignItems: isDesktop ? 'flex-start' : undefined,
				       justifyContent: isDesktop ? 'center' : undefined,
				       gap: isDesktop ? 32 : undefined,
				       position: 'relative',
			       }}>
				       <div id="Doglist" style={{
					       flex: isDesktop ? '0 0 380px' : undefined,
					       maxWidth: isDesktop ? 380 : undefined,
					       minWidth: isDesktop ? 320 : undefined,
					       width: isDesktop ? 380 : undefined,
					       margin: isDesktop ? '32px 0 32px 32px' : undefined,
					       overflowY: isDesktop ? 'auto' : undefined,
					       height: isDesktop ? 'calc(100vh - 64px)' : undefined,
					       background: isDesktop ? '#fafbfc' : undefined,
					       borderRadius: isDesktop ? 16 : undefined,
					       boxShadow: isDesktop ? '0 2px 16px rgba(0,0,0,0.06)' : undefined,
					       padding: isDesktop ? 16 : undefined,
				       }}>
						       {dogs.length === 0 ? (
							       <div>{t('doglist.empty') || 'No dogs found.'}</div>
						       ) : (
							       isDesktop ? (
								       <>
									       <CardSmall
										       key={(detailsDog?._id || dogs[0]._id)}
										       {...(detailsDog || dogs[0])}
										       canEdit={true}
										       onEdit={() => handleEdit(detailsDog || dogs[0])}
										       onRemove={() => handleRemove(detailsDog || dogs[0])}
										       onViewDetails={() => handleDetails(detailsDog || dogs[0])}
									       />
								       </>
							       ) : (
								       dogs.map(dog => (
									       <CardSmall
										       key={dog._id}
										       {...dog}
										       canEdit={true}
										       onEdit={() => handleEdit(dog)}
										       onRemove={() => handleRemove(dog)}
										       onViewDetails={() => handleDetails(dog)}
									       />
								       ))
							       )
						       )}
				       </div>
				       {editDog && (
					       <EditDogModal
						       dog={editDog}
						       isSingleDog={true}
						       onSave={handleEditSave}
						       onClose={() => setEditDog(null)}
					       />
				       )}
				       {removeDog && (
					       <RemoveDogModal
						       dog={removeDog}
						       onConfirm={handleRemoveConfirm}
						       onClose={() => setRemoveDog(null)}
					       />
				       )}
				       {isDesktop && detailsDog && (
					       <div style={{
						       flex: '1 1 0',
						       margin: '32px 32px 32px 0',
						       background: '#fff',
						       borderRadius: 16,
						       boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
						       padding: 24,
						       maxWidth: 700,
						       minWidth: 340,
						       overflow: 'auto',
						       position: 'relative',
					       }}>
						       <button onClick={()=>setDetailsDog(null)} style={{position:'absolute',top:12,right:12,width:40,height:40,background:'#e74c3c',color:'#fff',border:'none',borderRadius:'50%',fontSize:'1.5rem',fontWeight:'bold',cursor:'pointer',zIndex:2}} aria-label="Close" title="Close">×</button>
						       <DogDetails
							       {...detailsDog}
							       gender={detailsDog.gender === 'male' || detailsDog.gender === 'female' ? detailsDog.gender : undefined}
							       user={(detailsDog as any).user || (detailsDog as any).owner}
							       place={detailsDog.place}
							       location={detailsDog.location || detailsDog.place}
							       onClose={()=>setDetailsDog(null)}
						       />
					       </div>
				       )}
				       {/* Modal for mobile */}
				       {!isDesktop && detailsDog && (
					       <div style={{position:'fixed',top:0,left:0,width:'100vw',height:'100vh',background:'rgba(0,0,0,0.5)',zIndex:2147483647,display:'flex',alignItems:'center',justifyContent:'center'}}>
						       <div style={{background:'#fff',borderRadius:16,maxWidth:700,width:'95vw',maxHeight:'95vh',overflow:'auto',position:'relative',padding:24}}>
							       <button onClick={()=>setDetailsDog(null)} style={{position:'absolute',top:12,right:12,width:40,height:40,background:'#e74c3c',color:'#fff',border:'none',borderRadius:'50%',fontSize:'1.5rem',fontWeight:'bold',cursor:'pointer',zIndex:2}} aria-label="Close" title="Close">×</button>
							       <DogDetails
								       {...detailsDog}
								       gender={detailsDog.gender === 'male' || detailsDog.gender === 'female' ? detailsDog.gender : undefined}
								       user={(detailsDog as any).user || (detailsDog as any).owner}
								       place={detailsDog.place}
								       location={detailsDog.location || detailsDog.place}
								       onClose={()=>setDetailsDog(null)}
							       />
						       </div>
					       </div>
				       )}
			       </div>
		       );
}

export default DogList;
