import React, { useEffect, useState, useCallback } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../../contexts/AuthContext';
import '../../css/edit-modal.css';

interface EditDogFormData {
  name: string;
  breed?: string;
  color?: string;
  location?: string;
  age?: number;
  description?: string;
  size?: 'small' | 'medium' | 'large';
  gender?: 'male' | 'female';
  vaccinated?: boolean;
  neutered?: boolean;
}

interface EditDogModalProps {
  dog: any;
  onClose: () => void;
  onSave: (updatedDog: any) => void;
  modalPosition?: {x: number, y: number};
}

const getApiUrl = () => {
  if (process.env.REACT_APP_API_URL) return process.env.REACT_APP_API_URL;
  // Always use backend port 3002 to match server.js
  if (window.location.protocol === 'https:') {
    return `https://${window.location.hostname}:3002`;
  }
  // Localhost or LAN
  return `http://${window.location.hostname}:3002`;
};

const API_URL = getApiUrl();

const EditDogModal: React.FC<EditDogModalProps> = ({ dog, onClose, onSave, modalPosition }) => {
    const isMountedRef = React.useRef(true);
  const { t } = useTranslation();
  const { token } = useAuth();
  const { register, handleSubmit, setValue, formState: { errors } } = useForm<EditDogFormData>({
    defaultValues: {
      name: dog.name || '',
      breed: dog.breed || '',
      color: dog.color || '',
      location: dog.location || '',
      age: dog.age,
      description: dog.description || '',
      size: dog.size || 'medium',
      gender: dog.gender,
      vaccinated: !!dog.vaccinated,
      neutered: !!dog.neutered,
    }
  });
  const [submitting, setSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState<string | null>(null);
  const [mediaFiles, setMediaFiles] = useState<File[]>([]);
  const [mediaPreviews, setMediaPreviews] = useState<string[]>([]);
  // Deduplicate image variants: group by upload (same as DogDetails)
  // Group images by unique upload (timestamp+random) so each upload is represented once, pick best for device
  // For each upload, show only the variant appropriate for the current device
  const groupImagesByUpload = useCallback((images: any[]) => {
    const byUpload: Record<string, any[]> = {};
    images.forEach(img => {
      let key = img.url;
      const match = img.url.match(/image-(?:320|640|1024)-([\w-]+)(?:\.[a-zA-Z]+)?(?:\?|$)/);
      if (match) {
        key = match[1];
      } else {
        const fallback = img.url.match(/image-(?:320|640|1024)-(.*)/);
        if (fallback) key = fallback[1];
      }
      if (!byUpload[key]) byUpload[key] = [];
      byUpload[key].push(img);
    });
    // Determine device type and preferred width
    let preferredWidth = 640;
    if (typeof window !== 'undefined') {
      if (window.innerWidth <= 480) {
        preferredWidth = 320; // mobile
      } else if (window.innerWidth <= 900) {
        preferredWidth = 640; // tablet
      } else {
        preferredWidth = 1024; // desktop
      }
    }
    // For each upload, show only the closest variant to preferred width
    return Object.values(byUpload).map(variants => {
      let best = variants[0];
      let minDiff = Math.abs((best.width || 0) - preferredWidth);
      for (const v of variants) {
        const diff = Math.abs((v.width || 0) - preferredWidth);
        if (diff < minDiff) {
          best = v;
          minDiff = diff;
        }
      }
      return best;
    });
  }, []);
  const [existingImages, setExistingImages] = useState<any[]>(dog.images ? groupImagesByUpload(dog.images) : []);

  // Add modal-open class when component mounts, remove when unmounts
  useEffect(() => {
    // Don't lock body scroll - we need the overlay to scroll on mobile
    // Just prevent background content from scrolling
    const originalOverflow = document.body.style.overflow;
    const originalPosition = document.body.style.position;

    // On mobile, we want the overlay to scroll, not lock the body
    if (window.innerWidth <= 768) {
      document.body.style.position = 'fixed';
      document.body.style.width = '100%';
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.body.style.overflow = originalOverflow;
      document.body.style.position = originalPosition;
      document.body.style.width = '';
    };
  }, []);

  useEffect(() => {
    isMountedRef.current = true;
    // Prefill form with dog data only if dog._id changes
    if (!dog) return;
    Object.entries(dog).forEach(([key, value]) => {
      setValue(key as any, value);
    });
    // Only update images if they actually changed
    const grouped = dog.images ? groupImagesByUpload(dog.images) : [];
    setExistingImages(prev => {
      const prevUrls = prev.map(img => img.url).join(',');
      const newUrls = grouped.map(img => img.url).join(',');
      return prevUrls !== newUrls ? grouped : prev;
    });
    return () => {
      isMountedRef.current = false;
    };
  }, [dog?._id, setValue, groupImagesByUpload]);

  const onSubmit: SubmitHandler<EditDogFormData> = async (fields) => {
    try {
      if (isMountedRef.current) setSubmitting(true);
      if (isMountedRef.current) setSubmitError(null);
      let resp;
      const hasNewFiles = mediaFiles.length > 0;
      const hasRemovedImages = existingImages.length !== (dog.images ? dog.images.length : 0);
      const hasPhotoChanges = hasNewFiles || hasRemovedImages;
      // Timeout logic
      const fetchWithTimeout = (url, options, timeout = 20000) => {
        return Promise.race([
          fetch(url, options),
          new Promise((_, reject) => setTimeout(() => reject(new Error('Request timed out')), timeout))
        ]);
      };
      if (hasPhotoChanges) {
        const formData = new FormData();
        Object.entries(fields).forEach(([k, v]) => {
          if (typeof v !== 'undefined' && v !== '') formData.append(k, v as any);
        });
        // If all images removed, send keepImages as []
        const keepImagesData = JSON.stringify(existingImages.length === 0 ? [] : existingImages.map(img => img.url));
        formData.append('keepImages', keepImagesData);
        mediaFiles.forEach((file, idx) => {
          formData.append('media', file, file.name);
        });
        const headers: any = {};
        if (token) {
          headers['Authorization'] = `Bearer ${token}`;
        }
        resp = await fetchWithTimeout(`${API_URL}/api/dogs/${dog._id}`, {
          method: 'PATCH',
          body: formData,
          headers
        });
      } else {
        const bodyData: any = {};
        Object.entries(fields).forEach(([k, v]) => {
          if (typeof v !== 'undefined' && v !== '') bodyData[k] = v;
        });
        const headers: any = { 'Content-Type': 'application/json' };
        if (token) {
          headers['Authorization'] = `Bearer ${token}`;
        }
        resp = await fetchWithTimeout(`${API_URL}/api/dogs/${dog._id}`, {
          method: 'PATCH',
          headers,
          body: JSON.stringify(bodyData)
        });
      }
      if (!resp.ok) {
        const errData = await resp.json().catch(() => ({}));
        throw new Error(errData.message || 'Failed to update dog');
      }
      const updatedDog = await resp.json();
      if (isMountedRef.current) onSave(updatedDog);
      if (isMountedRef.current) onClose();
    } catch (err: any) {
