import React, { useState, useEffect } from 'react';
import { createPortal } from 'react-dom';
import { useSearchParams } from 'react-router-dom';
import EditDogModal from './EditDogModal';
import Search from '../Search';
import CardSmall from '../CardSmall';
import DogDetails from './DogDetails';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../../contexts/AuthContext';


const DogList: React.FC = () => {
  const { t } = useTranslation();
  const { user, token, isAuthenticated } = useAuth();
  const [dogs, setDogs] = useState<any[]>([]);
  const [filteredDogs, setFilteredDogs] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [editDog, setEditDog] = useState<any | null>(null);
  const [modalPosition, setModalPosition] = useState<{ x: number, y: number } | null>(null);
  const [selectedDog, setSelectedDog] = useState<any | null>(null);

  // TODO: Add fetch/filter logic and handlers here

  return (
    <main>
      {/* TODO: Add Search and all render logic here */}
      {editDog && createPortal(
        <EditDogModal
          dog={editDog}
          isSingleDog={dogs.length === 1}
          onSave={(updatedDog) => {
            // Example: update dog in list and close modal
            setDogs(dogs => dogs.map(d => d._id === updatedDog._id ? updatedDog : d));
            setEditDog(null);
          }}
        />,
        document.body
      )}
    </main>
  );
};

export default DogList;
