import React, { useEffect, useState } from 'react';
import CardSmall from '../CardSmall';
import EditDogModal from './EditDogModal';

interface DogType {
	_id: string;
	name: string;
	breed?: string;
	age?: number;
	gender?: string;
	place?: string;
	video?: any;
	thumbnail?: any;
	canEdit?: boolean;
	images?: any[];
	adoptionStatus?: string;
	adoptionQueue?: any[];
	vaccinated?: boolean;
}

const DogList: React.FC = () => {
	const [dogs, setDogs] = useState<DogType[]>([]);
	const [editDog, setEditDog] = useState<DogType | null>(null);

	useEffect(() => {
		fetch('http://localhost:3001/api/dogs')
			.then(res => res.json())
			.then(data => setDogs(data))
			.catch(() => setDogs([]));
	}, []);

	const handleEdit = (dog: DogType) => setEditDog(dog);
	const handleCloseModal = () => setEditDog(null);
	const handleDogUpdate = (updatedDog: DogType) => {
		setDogs(dogs => dogs.map(d => d._id === updatedDog._id ? updatedDog : d));
		setEditDog(null);
	};
	const handleRemove = (dogId: string) => {
		setDogs(dogs => dogs.filter(d => d._id !== dogId));
	};

	return (
		<div style={{ padding: 24 }}>
			<div style={{ display: 'flex', flexWrap: 'wrap', gap: 24, justifyContent: 'center' }}>
				{dogs.map(dog => (
					<CardSmall
						key={dog._id}
						{...dog}
						canEdit={true}
						onEdit={() => handleEdit(dog)}
						onRemove={() => handleRemove(dog._id)}
						onDogUpdate={handleDogUpdate}
						onViewDetails={() => handleEdit(dog)}
					/>
				))}
			</div>
			{editDog && (
				<EditDogModal
					dog={editDog}
					isSingleDog={true}
					onSave={(updatedDog) => {
						handleDogUpdate(updatedDog);
						handleCloseModal();
					}}
				/>
			)}
		</div>
	);
};

export default DogList;
