import React, { useEffect, useState } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';

interface EditDogFormData {
  name: string;
  breed?: string;
  age?: number;
  color?: string;
  description?: string;
  size?: string;
  gender?: 'male' | 'female';
  vaccinated?: boolean;
  neutered?: boolean;
  images?: string[];
}

interface EditDogModalProps {
  dog: any;
  isSingleDog: boolean;
  onSave: (dog: any) => void;
}

const EditDogModal: React.FC<EditDogModalProps> = ({ dog, isSingleDog, onSave }) => {
  const { t } = useTranslation();
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });
  const { register, handleSubmit } = useForm<EditDogFormData>({ defaultValues: dog });
  useEffect(() => {
    const handleResize = () => setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    console.log('[EditDogModal] Mounted with dog:', dog);
  }, [dog]);

  const onSubmit: SubmitHandler<EditDogFormData> = fields => {
    console.log('[EditDogModal] onSubmit called with fields:', fields);
    onSave({ ...dog, ...fields });
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      background: 'rgba(0,0,255,0.2)',
      zIndex: 999999,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      pointerEvents: 'auto',
    }}>
      <div style={{ position: 'relative', minWidth: '320px', minHeight: '220px', maxWidth: '90vw', maxHeight: '90vh' }}>
        <button
          type="button"
          onClick={() => {
            if (typeof window !== 'undefined' && window.dispatchEvent) {
              window.dispatchEvent(new CustomEvent('closeEditDogModal'));
            }
          }}
          style={{
            position: 'absolute',
            top: 12,
            right: 12,
            width: 36,
            height: 36,
            background: '#e74c3c',
            color: '#fff',
            border: 'none',
            borderRadius: '50%',
            fontSize: '1.5rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            zIndex: 1000000,
          }}
          aria-label="Close"
          title="Close"
        >
          Ã—
        </button>
        <form
          onSubmit={handleSubmit(onSubmit)}
          style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '1.2rem',
            fontSize: '1rem',
            background: 'white',
            border: '2px solid #3498db',
            borderRadius: 12,
            padding: '2rem',
            minWidth: '320px',
            minHeight: '220px',
            maxWidth: '90vw',
            maxHeight: '90vh',
            zIndex: 9999999,
            boxSizing: 'border-box',
            overflow: 'auto',
          }}
        >
          <label>{t('adddog.name')}</label>
          <input
            {...register('name')}
            defaultValue={dog.name || ''}
            required
            style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
          />
          <label>{t('adddog.breed')}</label>
          <input
            {...register('breed')}
            defaultValue={dog.breed || ''}
            style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }}
          />
          <label>{t('adddog.age') || 'Age'}</label>
          <input type="number" {...register('age')} defaultValue={dog.age || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.color') || 'Color'}</label>
          <input {...register('color')} defaultValue={dog.color || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.description') || 'Description'}</label>
          <textarea {...register('description')} defaultValue={dog.description || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.size') || 'Size'}</label>
          <input {...register('size')} defaultValue={dog.size || ''} style={{ padding: '0.5rem', borderRadius: 6, border: '1px solid #ccc', fontSize: '1rem', marginBottom: '1rem' }} />
          <label>{t('adddog.gender') || 'Gender'}</label>
          <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
            <label>
              <input type="radio" value="male" {...register('gender')} defaultChecked={dog.gender === 'male'} />
              {t('gender.male') || 'Male'}
            </label>
            <label>
              <input type="radio" value="female" {...register('gender')} defaultChecked={dog.gender === 'female'} />
              {t('gender.female') || 'Female'}
            </label>
          </div>
          <label>{t('adddog.images') || 'Images'}</label>
          <input type="file" multiple accept="image/*" {...register('images')} style={{ marginBottom: '1rem' }} />
          <label>{t('adddog.vaccinated') || 'Vaccinated'}</label>
          <input type="checkbox" {...register('vaccinated')} defaultChecked={!!dog.vaccinated} style={{ marginLeft: '0.5rem', marginBottom: '1rem' }} />
          <label>{t('adddog.neutered') || 'Neutered'}</label>
          <input type="checkbox" {...register('neutered')} defaultChecked={!!dog.neutered} style={{ marginLeft: '0.5rem', marginBottom: '1rem' }} />
          <div style={{
            display: 'flex',
            justifyContent: 'flex-end',
            marginTop: '1.5rem',
            position: 'sticky',
            bottom: 0,
            background: 'white',
            zIndex: 10,
            paddingBottom: 1
          }}>
            <button
              type="submit"
              style={{
                background: '#3498db',
                color: '#fff',
                border: 'none',
                borderRadius: 6,
                padding: '0.5rem 1.2rem',
                fontWeight: 'bold',
                fontSize: '1rem',
                cursor: 'pointer',
                boxShadow: '0 2px 8px rgba(52,152,219,0.08)'
              }}
            >
              {t('editdog.save').replace('editdog.', '').replace('adddog.', '') || 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditDogModal;
