import React, { useState, useEffect, useRef } from 'react';
import '../css/chat-app.css';
import io, { Socket } from 'socket.io-client';
import { useAuth } from '../contexts/AuthContext';

interface Message {
  _id: string;
  sender: string;
  recipient: string;
  message: string;
  sentAt: string;
}

interface Conversation {
  _id: string;
  participants: string[];
}

const ChatApp: React.FC = () => {
  const { user, token } = useAuth();
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConvo, setSelectedConvo] = useState<Conversation | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [otherUserId, setOtherUserId] = useState('');
  const [typing, setTyping] = useState(false);
  const [notification, setNotification] = useState<string | null>(null);
  const [userSearch, setUserSearch] = useState('');
  const [searchResults, setSearchResults] = useState<{_id: string, userName: string}[]>([]);
  const socketRef = useRef<ReturnType<typeof io> | null>(null);

  useEffect(() => {
    if (user?._id && token) {
      fetch(`/api/chat/conversations/${user._id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => setConversations(Array.isArray(data) ? data : []));
      // Connect to Socket.IO
      if (!socketRef.current) {
        socketRef.current = io('http://localhost:3001');
        socketRef.current.emit('join', user._id);
        socketRef.current.on('receiveMessage', (msg) => {
          setMessages(prev => [...prev, msg]);
        });
        socketRef.current.on('typing', ({ from }) => {
          setTyping(true);
          setTimeout(() => setTyping(false), 1500);
        });
        socketRef.current.on('notification', ({ from, message }) => {
          setNotification(`New message from ${from}: ${message}`);
          setTimeout(() => setNotification(null), 3000);
        });
      }
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, [user, token]);

  useEffect(() => {
    if (selectedConvo && token) {
      fetch(`/api/chat/messages/${selectedConvo._id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(setMessages);
    }
  }, [selectedConvo, token]);

  const startConversation = async () => {
    if (!otherUserId || !token) return;
    const res = await fetch('/api/chat/conversation', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ userId: user._id, otherUserId })
    });
    const convo = await res.json();
    setSelectedConvo(convo);
    setConversations(prev => [...prev, convo]);
    setOtherUserId('');
  };

  const sendMessage = async () => {
    if (!input || !selectedConvo || !token) return;
    // Send via Socket.IO
    const recipient = selectedConvo.participants.find(id => id !== user._id);
    socketRef.current?.emit('sendMessage', {
      conversationId: selectedConvo._id,
      sender: user._id,
      recipient,
      message: input
    });
    // Optionally, also persist to backend
    await fetch('/api/chat/message', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({
        conversationId: selectedConvo._id,
        sender: user._id,
        recipient,
        message: input
      })
    });
    setInput('');
  };

  // Typing indicator
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    if (selectedConvo) {
      const recipient = selectedConvo.participants.find(id => id !== user._id);
      socketRef.current?.emit('typing', { recipient, sender: user._id });
    }
  };

  // User search
  const handleUserSearch = async (e: React.ChangeEvent<HTMLInputElement>) => {
    setUserSearch(e.target.value);
    if (e.target.value.length > 1) {
      const res = await fetch(`/api/users/search?query=${encodeURIComponent(e.target.value)}`);
      const results = await res.json();
      setSearchResults(results);
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div className="chat-app-container">
      <div className="chat-app-sidebar">
        <input
          className="chat-app-search"
          type="text"
          value={userSearch}
          onChange={handleUserSearch}
          placeholder="Search users..."
        />
        {searchResults.length > 0 && (
          <ul style={{ listStyle: 'none', padding: 0, marginBottom: 4 }}>
            {searchResults.map(u => (
              <li key={u._id}>
                <button className="chat-app-convo-btn" onClick={() => { setOtherUserId(u._id); setUserSearch(''); setSearchResults([]); }}>{u.userName}</button>
              </li>
            ))}
          </ul>
        )}
        <input
          className="chat-app-search"
          type="text"
          value={otherUserId}
          onChange={e => setOtherUserId(e.target.value)}
          placeholder="User ID to chat"
        />
        <button className="chat-app-convo-btn" onClick={startConversation}>Start Chat</button>
        <div className="chat-app-convos">
          <b style={{ fontSize: 13, color: '#2196f3' }}>Conversations</b>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {conversations.map(convo => (
              <li key={convo._id}>
                <button
                  className={`chat-app-convo-btn${selectedConvo?._id === convo._id ? ' selected' : ''}`}
                  onClick={() => setSelectedConvo(convo)}
                >
                  {convo.participants.filter(id => id !== user._id).join(', ')}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="chat-app-main">
        <div className="chat-app-messages">
          {messages.map(msg => (
            <div key={msg._id} className={`chat-app-message${msg.sender === user._id ? ' self' : ''}`}>
              <span className={`chat-app-bubble${msg.sender === user._id ? ' self' : ''}`}>{msg.message}</span>
            </div>
          ))}
          {typing && <div className="chat-app-typing">Typing...</div>}
        </div>
        {selectedConvo && (
          <div className="chat-app-input-row">
            <input
              className="chat-app-input"
              type="text"
              value={input}
              onChange={handleInputChange}
              onKeyDown={e => { if (e.key === 'Enter') sendMessage(); }}
              placeholder="Type a message..."
            />
            <button
              className="chat-app-send-btn"
              onClick={sendMessage}
              disabled={!input.trim()}
            >Send</button>
          </div>
        )}
        {notification && (
          <div className="chat-app-notification">{notification}</div>
        )}
      </div>
    </div>
  );
};

export default ChatApp;
