import React, { useEffect, useState, useRef } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../../contexts/AuthContext';
import '../../css/edit-modal.css';
import '../../css/editdog-visuals.css';


interface EditDogFormData {
  name: string;
  breed?: string;
  color?: string;
  location?: string;
  age?: number;
  description?: string;
  size?: string;
  gender?: string;
  vaccinated?: boolean;
  neutered?: boolean;
}

type DogImage = { url: string };


interface EditDogModalProps {
  dog: any;
  onClose: () => void;
  onSave: (dog: any) => void;
  modalPosition?: { x: number; y: number };
}

const API_URL = process.env.REACT_APP_API_URL || '';

const getImageUrl = (url: string) => {
  if (!url) return '';
  if (/^https?:\/\//.test(url)) return url;
  if (url.startsWith('/')) return API_URL + url;
  return API_URL + '/uploads/dogs/' + url;
};


const EditDogModal = ({ dog, onClose, onSave, modalPosition }: EditDogModalProps) => {
  const { t } = useTranslation();
  const { token } = useAuth();
  const isMounted = useRef(true);
  const [mediaFiles, setMediaFiles] = useState<File[]>([]);
  const [mediaPreviews, setMediaPreviews] = useState<string[]>([]);
  const [existingImages, setExistingImages] = useState<DogImage[]>(dog.images ? [...dog.images] : []);
  const [selectedToDelete, setSelectedToDelete] = useState<Set<number>>(new Set());

  const { handleSubmit, setValue, reset } = useForm<EditDogFormData>({
    defaultValues: {
      name: dog.name || '',
      breed: dog.breed || '',
      color: dog.color || '',
      location: dog.location || '',
      age: dog.age || undefined,
      description: dog.description || '',
      size: dog.size || '',
      gender: dog.gender || '',
      vaccinated: !!dog.vaccinated,
      neutered: !!dog.neutered,
    }
  });

  useEffect(() => {
    isMounted.current = true;
    reset({
      name: dog.name || '',
      breed: dog.breed || '',
      color: dog.color || '',
      location: dog.location || '',
      age: dog.age || undefined,
      description: dog.description || '',
      size: dog.size || '',
      gender: dog.gender || '',
      vaccinated: !!dog.vaccinated,
      neutered: !!dog.neutered,
    });
    setExistingImages(dog.images ? [...dog.images] : []);
    setMediaFiles([]);
    setMediaPreviews([]);
    setSelectedToDelete(new Set());
    return () => {
      isMounted.current = false;
    };
  }, [dog, dog.images, setValue, reset]);

  const onSubmit: SubmitHandler<EditDogFormData> = async (fields) => {
    try {
      if (!isMounted.current) return;
      setSubmitting(true);
      setSubmitError(null);
      let resp;
      // Always send all fields, including unchecked checkboxes and empty fields
      const allKeys = [
        'name', 'breed', 'color', 'location', 'age', 'description', 'size', 'gender', 'vaccinated', 'neutered'
      ];
      // Always send correct keepImages (after deletions)
      const keepImagesArr = existingImages.map(img => img.url);
      const hasNewFiles = mediaFiles.length > 0;
      const hasRemovedImages = keepImagesArr.length !== (dog.images ? dog.images.length : 0);
      const hasPhotoChanges = hasNewFiles || hasRemovedImages;
      if (hasPhotoChanges) {
        const formData = new FormData();
        allKeys.forEach(k => {
          let v = fields[k as keyof EditDogFormData];
          // Always send as string, even for 0, false, or empty
          if (k === 'vaccinated' || k === 'neutered') {
            formData.append(k, v ? 'true' : 'false');
          } else if (typeof v === 'number') {
            formData.append(k, String(v));
          } else if (typeof v === 'boolean') {
            formData.append(k, v ? 'true' : 'false');
          } else if (typeof v !== 'undefined' && v !== null) {
            formData.append(k, String(v));
          } else {
            formData.append(k, '');
          }
        });
        formData.append('keepImages', JSON.stringify(keepImagesArr));
        mediaFiles.forEach((file, idx) => {
          formData.append('media', file, file.name);
        });
        const headers: any = {};
        if (token) headers['Authorization'] = `Bearer ${token}`;
        try {
          resp = await fetch(`${API_URL}/api/dogs/${dog._id}`, {
            method: 'PATCH',
            body: formData,
            headers
          });
        } catch (fetchErr) {
          if (isMounted.current) {
            setSubmitError('Failed to upload images. Please check your connection or try again.');
            setSubmitting(false);
          }
          return;
        }
      } else {
        const bodyData: any = {};
        allKeys.forEach(k => {
          let v = fields[k as keyof EditDogFormData];
          if (k === 'vaccinated' || k === 'neutered') {
            bodyData[k] = v ? true : false;
          } else if (typeof v !== 'undefined' && v !== null) {
            bodyData[k] = v;
          } else {
            bodyData[k] = '';
          }
        });
        // Always include keepImages in JSON PATCH
        bodyData.keepImages = existingImages.map(img => img.url);
        const headers: any = { 'Content-Type': 'application/json' };
        if (token) headers['Authorization'] = `Bearer ${token}`;
        resp = await fetch(`${API_URL}/api/dogs/${dog._id}`, {
          method: 'PATCH',
          headers,
          body: JSON.stringify(bodyData)
        });
      }
      if (!resp.ok) {
        const errData = await resp.json().catch(() => ({}));
        throw new Error(errData.message || 'Failed to update dog');
      }
      // Always fetch the latest dog data after update (not just PATCH response)
      let updatedDog = null;
      try {
        const respData = await resp.json();
        updatedDog = respData.dog || respData;
      } catch (e) {
        updatedDog = null;
      }
      // Defensive: fetch latest from backend in case PATCH response is stale
      if (updatedDog && updatedDog._id) {
        try {
          const latestResp = await fetch(`${API_URL}/api/dogs/${updatedDog._id}`);
          if (latestResp.ok) {
            updatedDog = await latestResp.json();
          }
        } catch {}
      }
      if (isMounted.current && updatedDog) {
        setExistingImages(updatedDog.images ? [...updatedDog.images] : []);
        setMediaFiles([]);
        setMediaPreviews([]);
        onSave(updatedDog);
        onClose();
      }
    } catch (err: any) {
      if (!isMounted.current) return;
      if (mediaFiles.length > 0 && (err.message === 'Failed to fetch' || err.message === 'NetworkError when attempting to fetch resource.')) {
        setSubmitError('Failed to upload images. Please check your connection or try again.');
      } else {
        setSubmitError(err.message || 'Failed to update dog');
      }
    } finally {
      if (isMounted.current) setSubmitting(false);
    }
  };

  return (
    <div 
      className="edit-modal-overlay"
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        zIndex: 2147483647,
        display: 'flex',
        alignItems: window.innerWidth > 768 ? 'center' : 'stretch',
        justifyContent: 'center',
        padding: window.innerWidth > 768 ? '2rem' : '0',
        boxSizing: 'border-box'
      }}
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          onClose();
        }
      }}
    >
      <div 
        style={{
          backgroundColor: 'white',
          borderRadius: window.innerWidth > 768 ? '12px' : '0',
          padding: '1.5rem',
          paddingTop: window.innerWidth > 768 ? '1.5rem' : '3.5rem',
          paddingBottom: window.innerWidth > 768 ? '1.5rem' : '6rem',
          width: window.innerWidth > 768 ? '100%' : '100vw',
          height: window.innerWidth > 768 ? 'auto' : '100vh',

          maxWidth: window.innerWidth > 768 ? '600px' : '100vw',
          maxHeight: window.innerWidth > 768 ? '90vh' : '100vh',
          overflowY: 'auto',
          WebkitOverflowScrolling: 'touch',
          position: 'relative',
          zIndex: 2147483647,
        }}
      >
        <button 
          className="editdog-close-btn"
          onClick={onClose}
        >
          &times;
        </button>
        <h2 style={{ 
          marginBottom: '1rem',
          position: window.innerWidth <= 768 ? 'sticky' : 'static',
          top: window.innerWidth <= 768 ? '0' : 'auto',
          backgroundColor: 'white',
          zIndex: 100,
          paddingTop: window.innerWidth <= 768 ? '0.5rem' : '0'
        }}>{t('editdog.title', { name: dog.name }) || `Edit ${dog.name}`}</h2>
        <form 
          onSubmit={handleSubmit(onSubmit)}
          style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
          }}
        >
          {/* Media preview and upload */}
          <label>{t('adddog.media') || 'Photos'}</label>
          <div className="media-input-row">
            <input
              id="editdog-media"
              name="media"
              type="file"
              accept="image/*"
              multiple
              onChange={e => {
                const files = Array.from(e.target.files || []);
                setMediaFiles(prev => [...prev, ...files]);
                setMediaPreviews(prev => [...prev, ...files.map(f => URL.createObjectURL(f))]);
              }}
            />
            {mediaPreviews.length > 0 && (
              <button 
                type="button" 
                onClick={() => {
                  setMediaFiles([]);
                  setMediaPreviews([]);
                }}
                className="editdog-reset-photos-btn"
              >
                {t('editdog.resetPhotos') || 'Reset New Photos'}
              </button>
            )}
            <div>
              {mediaPreviews.length > 0 ? (
                <>
                  <p style={{ margin: '0.5rem 0', color: '#e67e22', fontSize: '0.95rem', fontWeight: 'bold' }}>
                    {t('editdog.newPhotos') || 'New photos selected (will be added to existing). To remove old photos, uncheck or delete them below.'}
                  </p>
                  <div className="media-preview-list" style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {mediaPreviews.map((url, idx) => (
                      <img
                        key={idx}
                        src={url}
                        alt={`preview-${idx}`}
                        width={window.innerWidth <= 768 ? 120 : 80}
                        style={{ borderRadius: 8, marginTop: window.innerWidth <= 768 ? 12 : 0, objectFit: 'cover', height: 80 }}
                      />
                    ))}
                  </div>
                </>
              ) : existingImages.length > 0 ? (
                <>
                  <p style={{ margin: '0.5rem 0', color: '#666', fontSize: '0.9rem' }}>
                    {t('editdog.currentPhotos') || 'Current photos:'}
                  </p>
                  <div className="media-preview-list" style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {existingImages.map((img: any, idx: number) => (
                      <span key={idx} style={{ position: 'relative', display: 'inline-block', marginRight: 8 }}>
                        <img src={getImageUrl(img.url)} alt={`img-${idx}`} width={window.innerWidth <= 768 ? 120 : 80} style={{ borderRadius: 8, marginTop: window.innerWidth <= 768 ? 12 : 0, objectFit: 'cover', height: 80, opacity: selectedToDelete.has(idx) ? 0.5 : 1 }} />
                        <input
                          type="checkbox"
                          checked={selectedToDelete.has(idx)}
                          onChange={e => {
                            setSelectedToDelete(prev => {
                              const next = new Set(prev);
                              if (e.target.checked) next.add(idx);
                              else next.delete(idx);
                              return next;
                            });
                          }}
                          style={{ position: 'absolute', top: 4, left: 4, zIndex: 3, width: 22, height: 22, accentColor: '#e74c3c', background: '#fff', border: '2px solid #e74c3c', borderRadius: 6, boxShadow: '0 2px 8px rgba(0,0,0,0.12)' }}
                          title={selectedToDelete.has(idx) ? t('editdog.selectedToDelete') || 'Selected to delete' : t('editdog.selectToDelete') || 'Select to delete'}
                        />
                        <button
                          type="button"
                          style={{ position: 'absolute', top: 4, right: 4, background: '#e74c3c', color: '#fff', border: 'none', borderRadius: '50%', width: 22, height: 22, minWidth: 22, minHeight: 22, maxWidth: 22, maxHeight: 22, cursor: 'pointer', fontWeight: 'bold', fontSize: 16, lineHeight: '22px', padding: 0, display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: '0 2px 8px rgba(0,0,0,0.12)' }}
                          onClick={() => {
                            setExistingImages(existingImages.filter((_, i) => i !== idx));
                            setSelectedToDelete(prev => {
                              const next = new Set(prev);
                              next.delete(idx);
                              // Shift all indices above idx down by 1
                              const shifted = new Set<number>();
                              next.forEach(i => shifted.add(i > idx ? i - 1 : i));
                              return shifted;
                            });
                          }}
                          title={t('editdog.deleteThisImage') || 'Delete this image'}
                        >
                          &times;
                        </button>
                        {window.innerWidth <= 768 && (
                          <span style={{ position: 'absolute', bottom: 4, left: 4, background: 'rgba(255,255,255,0.85)', color: '#e74c3c', fontWeight: 'bold', fontSize: 12, borderRadius: 4, padding: '2px 6px', zIndex: 4 }}>
                            {selectedToDelete.has(idx) ? (t('editdog.selectedToDelete') || 'Selected to delete') : (t('editdog.tapToRemove') || 'Tap to remove')}
                          </span>
                        )}
                      </span>
                    ))}
                  </div>
                </>
              ) : null}
              {existingImages.length > 0 && (
                <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
                  <button
                    type="button"
                    style={{ background: '#e74c3c', color: '#fff', border: 'none', borderRadius: 6, padding: '0.5rem 1rem', cursor: 'pointer', fontWeight: 'bold' }}
                    disabled={selectedToDelete.size === 0}
                    onClick={() => {
                      setExistingImages(existingImages.filter((_, i) => !selectedToDelete.has(i)));
                      setSelectedToDelete(new Set());
                    }}
                  >
                    {t('editdog.deleteSelected') || 'Delete Selected'}
                  </button>
                  <button
                    type="button"
                    style={{ background: '#e67e22', color: '#fff', border: 'none', borderRadius: 6, padding: '0.5rem 1rem', cursor: 'pointer', fontWeight: 'bold' }}
                    onClick={() => {
                      setExistingImages([]);
                      setSelectedToDelete(new Set());
                    }}
                  >
                    {t('editdog.deleteAll') || 'Delete All'}
                  </button>
                </div>
              )}
              {existingImages.length === 0 && mediaPreviews.length === 0 && (
                <p style={{ margin: '0.5rem 0', color: '#999', fontSize: '0.9rem' }}>
                  {t('editdog.noPhotos') || 'No photos selected'}
                </p>
              )}
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditDogModal;
