import Header from "./components/Header";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";
import ErrorBoundary from './components/ErrorBoundary';
import ChatApp from './components/ChatApp';
import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Home from './components/pages/Home';
import AdddogForm from './components/pages/AdddogForm';
import About from './components/pages/About';
import LoginForm from './components/pages/LoginForm';
import RegisterForm from './components/pages/RegisterForm';
import "./css/mobile-fixes.css"; // Mobile viewport fixes


const App = () => {
  const [showChatModal, setShowChatModal] = useState(false);

  useEffect(() => {
    const handler = () => setShowChatModal(false);
    window.addEventListener('closeChatModal', handler);
    return () => window.removeEventListener('closeChatModal', handler);
  }, []);

  return (
    <ErrorBoundary>
      <div id="Wrap">
        <Header />
        <Navbar />
        <Routes>
          <Route path="/" element={<About />} />
          <Route path="/home" element={<Home />} />
          <Route path="/psi" element={<Home />} />
          <Route path="/adddog" element={<AdddogForm />} />
          <Route path="/dodajpsa" element={<AdddogForm />} />
          <Route path="/logiranje" element={<LoginForm />} />
          <Route path="/registracija" element={<RegisterForm />} />
        </Routes>
        <Footer onChatClick={() => setShowChatModal(true)} />
      </div>
      {showChatModal && (
        <div style={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          zIndex: 10010,
          boxShadow: '0 4px 24px rgba(33,150,243,0.18)',
          borderRadius: 16,
        }}>
          <div style={{ background: '#fff', borderRadius: 16, padding: 0, minWidth: 260, maxWidth: 340, position: 'relative' }} onClick={e => e.stopPropagation()}>
            <ChatApp />
          </div>
        </div>
      )}
    </ErrorBoundary>
  );
};

export default App;
