import Header from "./components/Header";
import Footer from "./components/Footer";
import Navbar from "./components/Navbar";
import ErrorBoundary from './components/ErrorBoundary';
import ChatApp from './components/ChatApp';
import React, { useState } from 'react';
import "./css/mobile-fixes.css"; // Mobile viewport fixes


const App = () => {
  const [showChatModal, setShowChatModal] = useState(false);

  // Pass setShowChatModal to Footer so it can open the chat modal
  return (
    <ErrorBoundary>
      <div id="Wrap">
        <Header />
        <Navbar />
        <Footer onChatClick={() => setShowChatModal(true)} />
      </div>
      {showChatModal && (
        <div style={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          zIndex: 10010,
          boxShadow: '0 4px 24px rgba(33,150,243,0.18)',
          borderRadius: 16,
        }}>
          <div style={{ background: '#fff', borderRadius: 16, padding: 0, minWidth: 260, maxWidth: 340 }} onClick={e => e.stopPropagation()}>
            <ChatApp />
            <button onClick={() => setShowChatModal(false)} style={{ margin: 8, marginTop: 0, background: '#2196f3', color: '#fff', border: 'none', borderRadius: 8, padding: '6px 18px', fontWeight: 600, fontSize: 13, cursor: 'pointer', float: 'right' }}>Close</button>
          </div>
        </div>
      )}
    </ErrorBoundary>
  );
};

export default App;
